import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { Router, RouterLink } from '@angular/router'; // Importa o Router

@Component({
  selector: 'app-splash-screen',
  standalone: true,
  imports: [CommonModule, RouterLink],
  templateUrl: './splash-screen.component.html',
  styleUrls: ['./splash-screen.component.css']
})
export class SplashScreenComponent {

  // Injeta o Router para podermos navegar
  constructor(private router: Router) {}

  // Função para navegar para o modo cliente (o cardápio)
  goToCliente(): void {
    // O cardápio agora está em '/cardapio'
    this.router.navigate(['/cardapio']);
  }

  // Função para navegar para o modo funcionário (o painel admin)
  goToFuncionario(): void {
    this.router.navigate(['/admin']);
  }
}
<!--
  Este HTML usa as classes do Tailwind CDN para recriar
  o Figma (image_8783d9.png).

  A classe 'splash-content' vem do seu CSS e ajuda a centrar tudo.
-->
<div class="splash-content text-center">

  <!-- 1. Logo (Estilizado como no Figma) -->
  <h1 class="text-7xl font-extrabold text-white mb-4" style="font-family: 'Inter', sans-serif;">
    iBeus
  </h1>
  <p class="text-xl text-gray-200 mb-12">
    Seja bem-vindo ao seu painel de delivery.
  </p>

  <!-- 2. Botões de Ação -->
  <div class="flex flex-col md:flex-row gap-8">

    <!-- Botão Modo Cliente -->
    <button (click)="goToCliente()"
            class="bg-white rounded-2xl p-8 w-72 flex flex-col items-center justify-center shadow-lg transition-all duration-300 hover:shadow-2xl hover:-translate-y-1">
      <!-- Ícone (Pessoa) -->
      <svg class="w-16 h-16 text-blue-600 mb-4" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor">
        <path stroke-linecap="round" stroke-linejoin="round" d="M15.75 6a3.75 3.75 0 1 1-7.5 0 3.75 3.75 0 0 1 7.5 0ZM4.501 20.118a7.5 7.5 0 0 1 14.998 0A17.933 17.933 0 0 1 12 21.75c-2.676 0-5.216-.584-7.499-1.632Z" />
      </svg>
      <span class="text-2xl font-bold text-gray-900">Modo Cliente</span>
      <span class="text-sm text-gray-500">Ver cardápio e fazer pedidos</span>
    </button>

    <!-- Botão Modo Funcionário -->
    <button (click)="goToFuncionario()"
            class="bg-white rounded-2xl p-8 w-72 flex flex-col items-center justify-center shadow-lg transition-all duration-300 hover:shadow-2xl hover:-translate-y-1">
      <!-- Ícone (Prancheta/Lista) -->
      <svg class="w-16 h-16 text-blue-600 mb-4" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor">
        <path stroke-linecap="round" stroke-linejoin="round" d="M9 12h3.75M9 15h3.75M9 18h3.75m3 .75H18a2.25 2.25 0 0 0 2.25-2.25V6.108c0-1.135-.845-2.098-1.976-2.192a48.424 48.424 0 0 0-1.123-.08m-5.801 0c-.065.21-.1.433-.1.664 0 .414.336.75.75.75h4.5a.75.75 0 0 0 .75-.75c0-.231-.035-.454-.1-.664M6.75 7.5H4.875c-1.125 0-2.062.938-2.062 2.063v10.875c0 1.125.938 2.063 2.063 2.063h10.875c1.125 0 2.063-.938 2.063-2.063V16.5m-13.5-9V3.375c0-1.125.938-2.063 2.063-2.063h4.125c1.125 0 2.063.938 2.063 2.063v4.125m-8.25 0h8.25" />
      </svg>
      <span class="text-2xl font-bold text-gray-900">Modo Funcionário</span>
      <span class="text-sm text-gray-500">Gerenciar pratos e pedidos</span>
    </button>

  </div>

</div>
