import { Component } from '@angular/core';
import { CommonModule } from '@angular/common'; // Importe CommonModule
import { RouterLink } from '@angular/router'; // Importe RouterLink
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';
import { CartService, CartItem } from '../../services/cart.service'; // Ajuste o caminho se precisar

@Component({
  selector: 'app-topbar', // Este √© o nome da tag que voc√™ vai usar
  standalone: true,
  templateUrl: './topbar.component.html',
  styleUrls: ['./topbar.component.css'],
  imports: [
    CommonModule, // Adicione aqui
    RouterLink    // Adicione aqui
  ],
  templateUrl: './topbar.component.html',
  styleUrls: ['./topbar.component.css']
})
export class TopbarComponent {

  public totalItensNoCarrinho$: Observable<number>;

  constructor(private cartService: CartService) {
    // Pega a l√≥gica do contador de itens
    this.totalItensNoCarrinho$ = this.cartService.items$.pipe(
      map((items: CartItem[]) => items.reduce((total: number, item: CartItem) => total + item.quantidade, 0))
    );
  }
}
