import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterLink } from '@angular/router';
import { Observable } from 'rxjs'; // Importa o Observable
import { map } from 'rxjs/operators'; // <-- 1. IMPORTAR O OPERATOR 'map'

// NOSSOS SERVIÇOS
import { Dish, DishService } from '../../services/dish.service';
import { CartService, CartItem } from '../../services/cart.service'; // <-- 1. IMPORTA O CART SERVICE

@Component({
  selector: 'app-dish-list',
  standalone: true,
  imports: [
    CommonModule,
    RouterLink
  ],
  templateUrl: './dish-list.component.html',
  styleUrls: ['./dish-list.component.css']
})
export class DishListComponent implements OnInit {

  dishes: Dish[] = [];
  isLoading = true;
  error: string | null = null;

  // Observables para o resumo do carrinho
  public totalItensNoCarrinho$: Observable<number>;
  public totalPrecoCarrinho$: Observable<number>;

  // 2. INJETA O CART SERVICE (além do DishService)
  constructor(
    private dishService: DishService,
    private cartService: CartService // <-- INJETADO AQUI
  ) {
    // 3. Conecta os Observables do carrinho para o HTML
    this.totalPrecoCarrinho$ = this.cartService.total$;
    this.totalItensNoCarrinho$ = this.cartService.items$.pipe(
      // Calcula o número total de itens (soma das quantidades)
      // 2. ADICIONA OS TIPOS (CartItem[], total, item) para o modo 'strict'
      map((items: CartItem[]) => items.reduce((total: number, item: CartItem) => total + item.quantidade, 0))
    );
  }

  ngOnInit(): void {
    this.loadDishes();
  }

  loadDishes(): void {
    this.isLoading = true;
    this.error = null;
    this.dishService.getDishes().subscribe({
      next: (data) => {
        this.dishes = data;
        this.isLoading = false;
        console.log('Pratos carregados:', data);
      },
      error: (err) => {
        console.error('Erro ao buscar pratos:', err);
        this.error = 'Não foi possível carregar o cardápio. O backend está rodando?';
        this.isLoading = false;
      }
    });
  }

  // 4. NOVO MÉTODO: Chamado quando o cliente clica em "Adicionar"
  adicionarAoCarrinho(dish: Dish): void {
    console.log('Adicionando ao carrinho:', dish.name);
    this.cartService.addItem(dish);
  }

  // (O método deleteDish pode ficar, é útil para o admin)
  deleteDish(id: number | undefined): void {
    if (!id) return;
    if (confirm('Tem certeza que deseja deletar este prato?')) {
      this.dishService.deleteDish(id).subscribe({
        next: () => {
          console.log('Prato deletado com sucesso');
          this.dishes = this.dishes.filter(dish => dish.id !== id);
        },
        error: (err) => {
          this.error = 'Erro ao deletar o prato.';
        }
      });
    }
  }
}
<!-- Cabeçalho (com o link "Adicionar Prato" que já tínhamos) -->
<div class="container mx-auto p-6 max-w-4xl">
  <div class="flex justify-between items-center mb-6">
    <h1 class="text-3xl font-bold text-gray-800">Cardápio</h1>

    <!-- Links de Ação -->
    <div class="flex flex-wrap gap-4">
      <!-- LINK NOVO PARA RASTREAMENTO -->
      <a [routerLink]="['/tracker']" class="bg-white border border-gray-300 hover:bg-gray-100 text-gray-800 font-bold py-2 px-4 rounded-lg shadow-sm transition duration-300">
        Acompanhar Pedido
      </a>
      <!-- Link de Admin -->
      <a [routerLink]="['/add-dish']" class="bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-lg shadow-md transition duration-300">
        Adicionar Prato (Admin)
      </a>
    </div>
  </div>

  <!-- Feedback de Carregamento -->
  <div *ngIf="isLoading" class="text-center py-10">
    <p class="text-gray-600 text-lg">Carregando cardápio...</p>
    <!-- (Opcional: Adicionar um spinner de CSS aqui) -->
  </div>

  <!-- Feedback de Erro -->
  <div *ngIf="error" class="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded-lg" role="alert">
    <strong>Erro:</strong> {{ error }}
  </div>

  <!-- Lista de Pratos (Dishes) -->
  <div *ngIf="!isLoading && !error && dishes.length > 0" class="grid grid-cols-1 md:grid-cols-2 gap-6">

    <!-- Loop *ngFor para cada 'dish' na nossa lista 'dishes' -->
    <div *ngFor="let dish of dishes" class="bg-white rounded-xl shadow-lg overflow-hidden flex flex-col">
      <!-- (Opcional: Imagem do Prato)
      <img src="https://placehold.co/600x400/eeeeee/cccccc?text=Foto+do+Prato" alt="{{ dish.name }}" class="w-full h-48 object-cover">
      -->

      <div class="p-6 flex-grow flex flex-col">
        <h2 class="text-2xl font-bold text-gray-800 mb-2">{{ dish.name }}</h2>
        <p class="text-gray-600 mb-4 flex-grow">{{ dish.description }}</p>

        <div class="flex justify-between items-center mt-4">
          <span class="text-2xl font-semibold text-green-600">{{ dish.price | currency:'BRL' }}</span>

          <!-- BOTÃO 'ADICIONAR AO CARRINHO' -->
          <button (click)="adicionarAoCarrinho(dish)"
                  class="bg-green-500 hover:bg-green-600 text-white font-bold py-2 px-4 rounded-lg shadow-md transition duration-300">
            Adicionar
          </button>
        </div>

        <!-- (Opcional: Botões de Admin para Editar/Deletar) -->
        <div class="border-t mt-4 pt-3 flex gap-2">
          <a [routerLink]="['/edit-dish', dish.id]" class="text-sm text-blue-500 hover:underline">Editar</a>
          <button (click)="deleteDish(dish.id)" class="text-sm text-red-500 hover:underline">Deletar</button>
        </div>
      </div>
    </div>

  </div>

  <!-- Mensagem de Cardápio Vazio -->
  <div *ngIf="!isLoading && !error && dishes.length === 0" class="text-center py-20 bg-white rounded-xl shadow-lg">
    <h2 class="text-2xl font-semibold text-gray-700 mb-4">Cardápio Vazio</h2>
    <p class="text-gray-500">Nenhum prato foi adicionado ainda.</p>
    <p class="text-gray-500 mt-2">Clique em "Adicionar Prato (Admin)" para começar.</p>
  </div>
</div>


<!-- BARRA DE CHECKOUT (FIXA NO FINAL) -->
<!-- Ela só aparece se o total de itens for maior que 0 -->
<div *ngIf="(totalItensNoCarrinho$ | async) as totalItens" class="sticky bottom-0 left-0 w-full bg-gray-900 text-white p-4 shadow-2xl-top" [class.hidden]="totalItens === 0">
  <div class="container mx-auto max-w-4xl flex justify-between items-center">
    <div>
      <h3 class="text-xl font-bold">Meu Pedido</h3>
      <p class="text-sm text-gray-300">
        {{ totalItens }} {{ totalItens === 1 ? 'item' : 'itens' }}
      </p>
    </div>

    <div class="flex items-center gap-6">
      <span class="text-2xl font-bold">
        Total: {{ (totalPrecoCarrinho$ | async) | currency:'BRL' }}
      </span>
      <a [routerLink]="['/checkout']" class="bg-green-500 hover:bg-green-600 text-white font-bold py-3 px-6 rounded-lg shadow-lg transition duration-300">
        Finalizar Pedido
      </a>
    </div>
  </div>
</div>
