import { ApplicationConfig, importProvidersFrom } from '@angular/core';
import { provideRouter, Routes } from '@angular/router';
import { HttpClientModule } from '@angular/common/http';
import { ReactiveFormsModule } from '@angular/forms'; // 1. Importa o ReactiveFormsModule (para todos os nossos formul치rios)

// Nossos Componentes
import { DishListComponent } from './components/dish-list/dish-list.component';
import { DishFormComponent } from './components/dish-form/dish-form.component';
import { CheckoutComponent } from './components/checkout/checkout.component';
import { OrderTrackerComponent } from './components/order-tracker/order-tracker.component';
import { AdminDashboardComponent } from './components/admin-dashboard/admin-dashboard.component'; // 2. Importa o novo componente Admin

const routes: Routes = [
  // Rotas do Card치pio e Admin (Pratos)
  { path: '', component: DishListComponent },
  { path: 'add-dish', component: DishFormComponent },
  { path: 'edit-dish/:id', component: DishFormComponent },

  // Rota da Cozinha (Admin - Pedidos)
  { path: 'admin', component: AdminDashboardComponent }, // 3. Adiciona a nova rota /admin

  // Rotas do Cliente (Pedido)
  { path: 'checkout', component: CheckoutComponent },
  { path: 'tracker', component: OrderTrackerComponent }
];

export const appConfig: ApplicationConfig = {
  providers: [
    provideRouter(routes),
    importProvidersFrom(
      HttpClientModule,
      ReactiveFormsModule // 4. Adiciona o ReactiveFormsModule aos providers
    )
  ]
};
