import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterLink } from '@angular/router';
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';

// NOSSOS SERVI√áOS
import { Dish, DishService } from '../../services/dish.service';
import { CartService, CartItem } from '../../services/cart.service';

@Component({
  selector: 'app-dish-list',
  standalone: true,
  imports: [
    CommonModule,
    RouterLink
  ],
  templateUrl: './dish-list.component.html',
  styleUrls: ['./dish-list.component.css']
})
export class DishListComponent implements OnInit {

  dishes: Dish[] = [];
  isLoading = true;
  error: string | null = null;

  public totalItensNoCarrinho$: Observable<number>;
  public totalPrecoCarrinho$: Observable<number>;

  constructor(
    private dishService: DishService,
    private cartService: CartService
  ) {
    this.totalPrecoCarrinho$ = this.cartService.total$;
    this.totalItensNoCarrinho$ = this.cartService.items$.pipe(
      map((items: CartItem[]) => items.reduce((total: number, item: CartItem) => total + item.quantidade, 0))
    );
  }

  ngOnInit(): void {
    this.loadDishes();
  }

  loadDishes(): void {
    this.isLoading = true;
    this.error = null;
    this.dishService.getDishes().subscribe({
      next: (data) => {
        this.dishes = data;
        this.isLoading = false;
        console.log('Pratos carregados:', data);
      },
      error: (err) => {
        console.error('Erro ao buscar pratos:', err);
        this.error = 'N√£o foi poss√≠vel carregar o card√°pio. O backend est√° rodando?';
        this.isLoading = false;
      }
    });
  }

  adicionarAoCarrinho(dish: Dish): void {
    console.log('Adicionando ao carrinho:', dish.name);
    this.cartService.addItem(dish);
  }

  // REMOVIDO: O m√©todo deleteDish() foi removido daqui.
}
<!-- Barra Bem-vindo -->
<div class="mx-auto mt-6 mb-4 w-[88%] rounded-md bg-[#ff6f5e] text-white px-4 py-3 flex items-center gap-2">
  <span>Bem-vindo!</span>
  <span class="inline-block w-4 h-4">üáßüá∑</span>
</div>

<!-- Navbar superior -->
<nav class="mx-auto w-[88%] mb-8 flex items-center gap-2">
  <a class="px-4 py-2 rounded-md bg-[#f6f6f6] border border-[#e8e8e8] text-gray-700 font-medium hover:bg-white transition">
    Card√°pio
  </a>

  <a [routerLink]="['/checkout']"
     class="px-4 py-2 rounded-md bg-[#f6f6f6] border border-[#e8e8e8] text-gray-700 font-medium hover:bg-white transition">
     Carrinho ({{ (totalItensNoCarrinho$ | async) ?? 0 }})
  </a>

  <a [routerLink]="['/tracker']"
     class="px-4 py-2 rounded-md bg-[#f6f6f6] border border-[#e8e8e8] text-gray-700 font-medium hover:bg-white transition">
     Meus Pedidos
  </a>

  <button (click)="sair()"
          class="ml-auto px-4 py-2 rounded-md bg-[#ff6f5e] text-white font-medium hover:opacity-90 transition">
    Sair
  </button>
</nav>

<!-- T√≠tulo + subt√≠tulo -->
<section class="mx-auto w-[88%] text-center">
  <h1 class="text-3xl font-extrabold text-gray-900">Card√°pio</h1>
  <p class="text-gray-600 mt-2">Escolha seus pratos favoritos</p>
</section>

<!-- Estados -->
<div *ngIf="isLoading" class="text-center py-20">Carregando‚Ä¶</div>
<div *ngIf="error" class="mx-auto w-[88%] bg-red-100 text-red-700 px-4 py-3 rounded-md my-6">
  {{ error }}
</div>
<div *ngIf="!isLoading && !error && dishes.length === 0"
     class="text-center py-20 text-gray-500">
  Nenhum prato cadastrado.
</div>

<!-- Grid de pratos -->
<section *ngIf="!isLoading && !error && dishes.length > 0"
         class="mx-auto w-[88%] my-8 grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">

  <article *ngFor="let dish of dishes; trackBy: trackByDish"
           class="bg-white rounded-xl shadow-sm border border-[#eee] overflow-hidden">

    <!-- Topo amarelo com √≠cone -->
    <div class="bg-[#ffd114] h-48 flex items-center justify-center">
      <img [src]="dish.imageUrl || 'https://placehold.co/120x120?text=%F0%9F%8D%B4'"
           (error)="onImgFallback($event)"
           alt="√çcone do prato"
           class="w-24 h-24 object-contain" />
    </div>

    <!-- Corpo do card -->
    <div class="p-6">
      <h3 class="text-lg font-semibold text-gray-900">{{ dish.name }}</h3>
      <p class="text-sm text-gray-600 mt-1">{{ dish.description }}</p>

      <div class="mt-3">
        <span class="text-sm text-gray-500 block">Pre√ßo</span>
        <strong class="text-[#1db954]">
          {{ dish.price | currency:'BRL' }}
        </strong>
      </div>

      <button (click)="adicionarAoCarrinho(dish)"
              class="mt-4 w-full rounded-md bg-[#ff6f5e] text-white font-semibold py-2 hover:opacity-90 transition">
        Adicionar ao Carrinho
      </button>
    </div>
  </article>
</section>

<!-- Barra fixa de checkout -->
<ng-container *ngIf="(totalItensNoCarrinho$ | async) as totalItens">
  <div *ngIf="totalItens > 0"
       class="fixed bottom-0 left-0 right-0 bg-gradient-to-r from-blue-600 to-blue-800 text-white p-4 shadow-2xl-top animate-slide-up">
    <div class="mx-auto w-[88%] flex justify-between items-center">
      <div>
        <span class="font-bold text-lg">
          {{ totalItens }} {{ totalItens === 1 ? 'item' : 'itens' }} no carrinho
        </span>
        <span class="ml-4 text-base">
          {{ (totalPrecoCarrinho$ | async) | currency:'BRL' }}
        </span>
      </div>

      <a [routerLink]="['/checkout']"
         class="bg-white text-blue-700 font-bold py-2 px-6 rounded-lg shadow-md hover:bg-gray-100 transition">
        Finalizar Pedido
      </a>
    </div>
  </div>
</ng-container>

<!-- Estilinhos r√°pidos para a barra -->
<style>
  @keyframes slide-up { from { transform: translateY(100%); opacity: 0; }
                        to   { transform: translateY(0);    opacity: 1; } }
  .animate-slide-up { animation: slide-up .25s ease-out; }
  .shadow-2xl-top { box-shadow: 0 -10px 24px rgba(2,6,23,.12); }
</style>
