import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterLink } from '@angular/router';
import { Observable } from 'rxjs'; // Importa o Observable
import { map } from 'rxjs/operators'; // <-- 1. IMPORTAR O OPERATOR 'map'

// NOSSOS SERVIÇOS
import { Dish, DishService } from '../../services/dish.service';
import { CartService, CartItem } from '../../services/cart.service'; // <-- 1. IMPORTA O CART SERVICE

@Component({
  selector: 'app-dish-list',
  standalone: true,
  imports: [
    CommonModule,
    RouterLink
  ],
  templateUrl: './dish-list.component.html',
  styleUrls: ['./dish-list.component.css']
})
export class DishListComponent implements OnInit {

  dishes: Dish[] = [];
  isLoading = true;
  error: string | null = null;

  // Observables para o resumo do carrinho
  public totalItensNoCarrinho$: Observable<number>;
  public totalPrecoCarrinho$: Observable<number>;

  // 2. INJETA O CART SERVICE (além do DishService)
  constructor(
    private dishService: DishService,
    private cartService: CartService // <-- INJETADO AQUI
  ) {
    // 3. Conecta os Observables do carrinho para o HTML
    this.totalPrecoCarrinho$ = this.cartService.total$;
    this.totalItensNoCarrinho$ = this.cartService.items$.pipe(
      // Calcula o número total de itens (soma das quantidades)
      // 2. ADICIONA OS TIPOS (CartItem[], total, item) para o modo 'strict'
      map((items: CartItem[]) => items.reduce((total: number, item: CartItem) => total + item.quantidade, 0))
    );
  }

  ngOnInit(): void {
    this.loadDishes();
  }

  loadDishes(): void {
    this.isLoading = true;
    this.error = null;
    this.dishService.getDishes().subscribe({
      next: (data) => {
        this.dishes = data;
        this.isLoading = false;
        console.log('Pratos carregados:', data);
      },
      error: (err) => {
        console.error('Erro ao buscar pratos:', err);
        this.error = 'Não foi possível carregar o cardápio. O backend está rodando?';
        this.isLoading = false;
      }
    });
  }

  // 4. NOVO MÉTODO: Chamado quando o cliente clica em "Adicionar"
  adicionarAoCarrinho(dish: Dish): void {
    console.log('Adicionando ao carrinho:', dish.name);
    this.cartService.addItem(dish);
  }

  // (O método deleteDish pode ficar, é útil para o admin)
  deleteDish(id: number | undefined): void {
    if (!id) return;
    if (confirm('Tem certeza que deseja deletar este prato?')) {
      this.dishService.deleteDish(id).subscribe({
        next: () => {
          console.log('Prato deletado com sucesso');
          this.dishes = this.dishes.filter(dish => dish.id !== id);
        },
        error: (err) => {
          this.error = 'Erro ao deletar o prato.';
        }
      });
    }
  }
}
<!-- Cabeçalho (com o link "Adicionar Prato" que já tínhamos) -->
<div class="container mx-auto p-6 max-w-4xl">
  <div class="flex justify-between items-center mb-6">
    <h1 class="text-3xl font-bold text-gray-800">Cardápio</h1>
    <a [routerLink]="['/add-dish']" class="bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-lg shadow-md transition duration-300">
      Adicionar Prato (Admin)
    </a>
  </div>

  <!-- Feedback de Erro -->
  <div *ngIf="error" class="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded-lg relative mb-6" role="alert">
    <strong class="font-bold">Erro: </strong>
    <span class="block sm:inline">{{ error }}</span>
  </div>

  <!-- Feedback de Carregamento -->
  <div *ngIf="isLoading" class="text-center py-10">
    <p class="text-gray-600 text-lg">Carregando cardápio...</p>
  </div>

  <!-- Lista de Pratos (Grid) -->
  <div *ngIf="!isLoading && dishes.length > 0" class="grid grid-cols-1 md:grid-cols-2 gap-6">

    <!-- Loop *ngFor para cada prato -->
    <div *ngFor="let dish of dishes" class="bg-white rounded-xl shadow-lg overflow-hidden flex flex-col">
      <div class="p-6 flex-grow">
        <div class="flex justify-between items-start">
          <div>
            <h2 class="text-2xl font-semibold text-gray-900 mb-2">{{ dish.name }}</h2>
            <p class="text-gray-600 mb-4">{{ dish.description }}</p>
          </div>
          <span class="text-xl font-bold text-green-600 ml-4">{{ dish.price | currency:'BRL' }}</span>
        </div>
      </div>

      <!-- === MUDANÇA PRINCIPAL AQUI === -->
      <div class="bg-gray-50 p-4 flex justify-between items-center">
        <!-- Botões de Admin (Editar/Deletar) -->
        <div>
          <a [routerLink]="['/edit-dish', dish.id]" class="text-sm font-medium text-blue-600 hover:text-blue-800 transition duration-300 mr-3">
            Editar
          </a>
          <button (click)="deleteDish(dish.id)" class="text-sm font-medium text-red-600 hover:text-red-800 transition duration-300">
            Deletar
          </button>
        </div>

        <!-- Botão "Adicionar ao Carrinho" (chama o novo método) -->
        <button (click)="adicionarAoCarrinho(dish)" class="bg-green-500 hover:bg-green-600 text-white font-semibold py-2 px-4 rounded-lg shadow transition duration-300">
          Adicionar ao Carrinho
        </button>
      </div>
    </div>
  </div>

  <!-- Mensagem de "Nenhum prato encontrado" -->
  <div *ngIf="!isLoading && dishes.length === 0 && !error" class="text-center py-10 bg-white rounded-lg shadow-md">
    <h2 class="text-2xl font-semibold text-gray-700 mb-2">Cardápio Vazio</h2>
    <p class="text-gray-500">Nenhum prato foi cadastrado ainda. Clique em "Adicionar Prato" para começar.</p>
  </div>

</div>


<!-- === BARRA DE CHECKOUT (NOVA) === -->
<!-- Fica "colada" na parte de baixo da tela -->
<div class="sticky bottom-0 left-0 w-full bg-gray-900 text-white shadow-lg-top p-4"
     *ngIf="(totalItensNoCarrinho$ | async) ?? 0 > 0">
  <div class="container mx-auto max-w-4xl flex justify-between items-center">
    <div>
      <span class="font-semibold">{{ (totalItensNoCarrinho$ | async) }} {{ (totalItensNoCarrinho$ | async) == 1 ? 'item' : 'itens' }} no carrinho</span>
      <span class="mx-2">|</span>
      <span class="font-bold text-lg">{{ (totalPrecoCarrinho$ | async) | currency:'BRL' }}</span>
    </div>

    <!-- Botão que leva para a rota /checkout -->
    <a [routerLink]="['/checkout']" class="bg-green-500 hover:bg-green-600 text-white font-bold py-2 px-5 rounded-lg shadow-md transition duration-300">
      Finalizar Pedido
    </a>
  </div>
</div>
