import { Component, OnInit, OnDestroy } from '@angular/core';
import { CommonModule } from '@angular/common';
import { Subscription } from 'rxjs';
import { TopbarComponent } from '../topbar/topbar.component';
import { PedidoService } from '../../../services/pedido.service';
import type { PedidoCompleto } from '../../../services/pedido.service';

@Component({
  selector: 'app-order-tracker',
  standalone: true,
  imports: [CommonModule, TopbarComponent],
  templateUrl: './order-tracker.component.html',
  styleUrls: ['./order-tracker.component.css']
})
export class OrderTrackerComponent implements OnInit, OnDestroy {

  pedidos: PedidoCompleto[] = [];
  isLoading = false;
  error: string | null = null;

  private pedidosSub!: Subscription;

  constructor(private pedidoService: PedidoService) {}

  ngOnInit(): void {
    this.isLoading = true;

    // Primeiro carrega os pedidos, depois se inscreve para receber atualiza√ß√µes
    this.pedidoService.loadAllPedidos();
    
    // Escuta o BehaviorSubject com todos os pedidos
    this.pedidosSub = this.pedidoService.pedidos$.subscribe({
      next: (data: PedidoCompleto[]) => {
        // Filtra apenas pedidos v√°lidos com status
        this.pedidos = data
          .filter(pedido => pedido && pedido.status)
          .sort((a, b) => (b.id > a.id ? 1 : -1));
        this.isLoading = false;
      },
      error: (err: any) => {
        console.error('Erro ao carregar pedidos:', err);
        this.error = 'N√£o foi poss√≠vel carregar os pedidos.';
        this.isLoading = false;
      }
    });
  }

  ngOnDestroy(): void {
    if (this.pedidosSub) this.pedidosSub.unsubscribe();
  }

  formatarStatus(status: string | undefined | null): string {
    if (!status) return 'Status indefinido';

    switch (status) {
      case 'RECEBIDO': return 'üìù Pedido Recebido';
      case 'EM_PREPARO': return 'üë®‚Äçüç≥ Em Preparo';
      case 'PRONTO': return '‚úÖ Pronto';
      case 'SAIU_PARA_ENTREGA': return 'üöö Saiu para Entrega'; // Corrigido typo: ENTREGRA -> ENTREGA
      case 'ENTREGUE': return '‚úÖ Entregue';
      case 'CANCELADO': return '‚ùå Cancelado';
      default: return status.replace(/_/g, ' '); // Substitui todos os underscores
    }
  }
}
<app-topbar></app-topbar>

<main class="max-w-5xl mx-auto p-6">
  <h1 class="text-3xl font-bold text-gray-800 mb-6 text-center">
    Acompanhe seu Pedido
  </h1>

  <!-- Loading -->
  <div *ngIf="isLoading" class="text-center text-gray-500 text-lg">
    Carregando pedidos...
  </div>

  <!-- Erro -->
  <div *ngIf="error" class="bg-red-100 text-red-700 p-4 rounded-lg text-center mb-6">
    {{ error }}
  </div>

  <!-- Lista de Pedidos -->
  <div *ngIf="!isLoading && pedidos.length > 0" class="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
    <div *ngFor="let pedido of pedidos" class="bg-white rounded-2xl shadow-md p-6 border border-gray-100 hover:shadow-lg transition">
      <h2 class="text-xl font-semibold text-gray-800 mb-3">
        Pedido #{{ pedido.id }}
      </h2>

      <div class="space-y-2 text-gray-700">
        <p><strong>Cliente:</strong> {{ pedido.nomeCliente }}</p>
        <p><strong>Telefone:</strong> {{ pedido.telefoneCliente }}</p>
        <p><strong>Endere√ßo:</strong> {{ pedido.enderecoCliente }}</p>
        <p><strong>Total:</strong> {{ pedido.total | currency:'BRL' }}</p>
      </div>

      <div class="mt-4 border-t pt-3">
        <strong class="text-gray-700">Status:</strong>
        <span class="ml-2 font-bold"
              [ngClass]="{
                'text-blue-600': pedido.status === 'RECEBIDO',
                'text-yellow-600': pedido.status === 'EM_PREPARO',
                'text-green-600': pedido.status === 'PRONTO' || pedido.status === 'SAIU_PARA_ENTREGRA' || pedido.status === 'ENTREGUE',
                'text-red-600': pedido.status === 'CANCELADO'
              }">
          {{ formatarStatus(pedido.status) }}
        </span>
      </div>
    </div>
  </div>

  <!-- Caso n√£o tenha pedidos -->
  <div *ngIf="!isLoading && pedidos.length === 0" class="text-center text-gray-500 text-lg mt-10">
    Nenhum pedido encontrado.
  </div>
</main>
