import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterLink } from '@angular/router';
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';

// NOSSOS SERVI√áOS
import { Dish, DishService } from '../../services/dish.service';
import { CartService, CartItem } from '../../services/cart.service';

@Component({
  selector: 'app-dish-list',
  standalone: true,
  imports: [
    CommonModule,
    RouterLink
  ],
  templateUrl: './dish-list.component.html',
  styleUrls: ['./dish-list.component.css']
})
export class DishListComponent implements OnInit {

  dishes: Dish[] = [];
  isLoading = true;
  error: string | null = null;

  public totalItensNoCarrinho$: Observable<number>;
  public totalPrecoCarrinho$: Observable<number>;

  constructor(
    private dishService: DishService,
    private cartService: CartService
  ) {
    this.totalPrecoCarrinho$ = this.cartService.total$;
    this.totalItensNoCarrinho$ = this.cartService.items$.pipe(
      map((items: CartItem[]) => items.reduce((total: number, item: CartItem) => total + item.quantidade, 0))
    );
  }

  ngOnInit(): void {
    this.loadDishes();
  }

  loadDishes(): void {
    this.isLoading = true;
    this.error = null;
    this.dishService.getDishes().subscribe({
      next: (data) => {
        this.dishes = data;
        this.isLoading = false;
        console.log('Pratos carregados:', data);
      },
      error: (err) => {
        console.error('Erro ao buscar pratos:', err);
        this.error = 'N√£o foi poss√≠vel carregar o card√°pio. O backend est√° rodando?';
        this.isLoading = false;
      }
    });
  }

  adicionarAoCarrinho(dish: Dish): void {
    console.log('Adicionando ao carrinho:', dish.name);
    this.cartService.addItem(dish);
  }

  // REMOVIDO: O m√©todo deleteDish() foi removido daqui.
}
<!-- TOP BAR (estilo Figma) -->
<header class="bg-[#ff6b5a] text-white">
  <div class="mx-auto max-w-6xl px-4">
    <div class="flex items-center justify-between h-14">
      <div class="flex items-center gap-2">
        <span class="text-sm opacity-90">Bem-vindo!</span>
        <span aria-hidden="true">üçΩÔ∏è</span>
      </div>

      <nav class="flex items-center gap-2">
        <a [routerLink]="['/']"
           class="px-3 py-1.5 rounded-md bg-white/10 hover:bg-white/20 transition">Card√°pio</a>

        <a [routerLink]="['/checkout']"
           class="px-3 py-1.5 rounded-md bg-white text-[#ff6b5a] hover:opacity-90 transition font-medium">
          Carrinho (<span>{{ (totalItensNoCarrinho$ | async) ?? 0 }}</span>)
        </a>

        <a [routerLink]="['/pedidos']"
           class="px-3 py-1.5 rounded-md bg-white/10 hover:bg-white/20 transition">Meus Pedidos</a>

        <button type="button"
                class="px-3 py-1.5 rounded-md border border-white/60 hover:bg-white/10 transition">
          Sair
        </button>
      </nav>
    </div>
  </div>
</header>

<!-- T√çTULO + SUB -->
<section class="mx-auto max-w-6xl px-4 pt-8">
  <h1 class="text-3xl font-semibold text-center text-gray-900">Card√°pio</h1>
  <p class="text-center text-gray-500 mt-1">Escolha seus pratos favoritos</p>
</section>

<!-- ESTADOS -->
<div *ngIf="isLoading" class="py-20 text-center text-gray-500">Carregando‚Ä¶</div>
<div *ngIf="error" class="mx-auto max-w-3xl mt-6 bg-red-100 text-red-700 p-4 rounded-lg">
  {{ error }}
</div>

<!-- GRID DE PRATOS -->
<section *ngIf="!isLoading && !error && dishes?.length"
         class="mx-auto max-w-6xl px-4 py-8 grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">

  <article *ngFor="let dish of dishes"
           class="rounded-xl border border-gray-200 bg-white shadow-sm hover:shadow-lg transition overflow-hidden flex flex-col">

    <!-- FAIXA AMARELA COM √çCONE (estilo Figma) -->
    <div class="h-44 bg-[#ffd100] grid place-items-center">
      <img src="assets/plate-icon.svg"
           alt=""
           class="w-16 h-16 opacity-90"
           onerror="this.style.display='none'">
    </div>

    <!-- CONTE√öDO -->
    <div class="p-5 flex flex-col gap-2 flex-1">
      <h3 class="text-lg font-semibold text-gray-900">{{ dish.name }}</h3>
      <p class="text-gray-600 text-sm flex-1">{{ dish.description }}</p>

      <div class="text-[#17a34a] font-bold text-lg">
        {{ dish.price | currency:'BRL' }}
      </div>

      <button (click)="adicionarAoCarrinho(dish)"
              class="mt-2 w-full rounded-lg bg-[#ff6b5a] text-white font-medium py-2.5 hover:brightness-95 transition">
        Adicionar ao Carrinho
      </button>
    </div>
  </article>
</section>

<!-- VAZIO -->
<section *ngIf="!isLoading && !error && (!dishes || dishes.length === 0)"
         class="mx-auto max-w-6xl px-4 py-24 text-center">
  <h2 class="text-2xl font-semibold text-gray-800">Card√°pio vazio</h2>
  <p class="text-gray-500 mt-2">Ainda n√£o h√° pratos cadastrados.</p>
</section>

<!-- BARRA FIXA DE CHECKOUT -->
<ng-container *ngIf="(totalItensNoCarrinho$ | async) as totalItens">
  <div *ngIf="totalItens > 0"
       class="fixed bottom-0 inset-x-0 bg-gradient-to-r from-[#ff6b5a] to-[#ff7f6d] text-white">
    <div class="mx-auto max-w-6xl px-4 h-16 flex items-center justify-between">
      <div class="flex items-baseline gap-4">
        <span class="font-semibold text-lg">{{ totalItens }} {{ totalItens === 1 ? 'item' : 'itens' }} no carrinho</span>
        <span class="text-base opacity-90">{{ (totalPrecoCarrinho$ | async) | currency:'BRL' }}</span>
      </div>
      <a [routerLink]="['/checkout']"
         class="rounded-md bg-white text-[#ff6b5a] font-semibold px-5 py-2 hover:opacity-90 transition">
        Finalizar Pedido
      </a>
    </div>
  </div>
</ng-container>
