import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormBuilder, FormGroup, Validators, ReactiveFormsModule } from '@angular/forms';
import { Router } from '@angular/router';
import { Observable } from 'rxjs';

// Nossos serviços
import { CartService, CartItem } from '../../services/cart.service';
import { PedidoService, ItemPedido, Pedido } from '../../services/pedido.service';

@Component({
  selector: 'app-checkout',
  standalone: true,
  imports: [
    CommonModule,
    ReactiveFormsModule // Precisamos de formulários reativos aqui
  ],
  templateUrl: './checkout.component.html',
  styleUrls: ['./checkout.component.css']
})
export class CheckoutComponent implements OnInit {

  customerForm: FormGroup;
  items$: Observable<CartItem[]>; // "Ouvinte" para os itens do carrinho
  total$: Observable<number>;     // "Ouvinte" para o total do carrinho

  isLoading = false;
  error: string | null = null;
  successMessage: string | null = null;

  constructor(
    private fb: FormBuilder,
    private cartService: CartService,
    private pedidoService: PedidoService,
    private router: Router
  ) {
    // Inicializa o formulário do cliente
    this.customerForm = this.fb.group({
      nomeCliente: ['', Validators.required],
      telefoneCliente: ['', Validators.required],
      enderecoCliente: ['', Validators.required]
    });

    // Conecta os Observables do serviço ao nosso componente
    this.items$ = this.cartService.items$;
    this.total$ = this.cartService.total$;
  }

  ngOnInit(): void {
    // Se o carrinho estiver vazio, redireciona para o cardápio
    if (this.cartService.getCurrentItems().length === 0) {
      this.router.navigate(['/']); // (Vamos mudar para '/menu' depois)
    }
  }

  // Chamado quando o cliente clica em "Finalizar Pedido"
  onSubmit(): void {
    if (this.customerForm.invalid) {
      this.customerForm.markAllAsTouched();
      return;
    }

    if (this.cartService.getCurrentItems().length === 0) {
      this.error = "Seu carrinho está vazio!";
      return;
    }

    this.isLoading = true;
    this.error = null;
    this.successMessage = null;

    // 1. Formata os itens do carrinho para o formato que o Backend (Java) espera
    const itensParaApi: ItemPedido[] = this.cartService.getCurrentItems().map(item => ({
      dish: { id: item.dish.id! }, // Envia apenas o ID do prato
      quantidade: item.quantidade
    }));

    // 2. Monta o objeto Pedido completo
    const novoPedido: Pedido = {
      ...this.customerForm.value, // Pega (nome, telefone, endereco) do formulário
      itens: itensParaApi
    };

    console.log('Enviando pedido para a API:', novoPedido);

    // 3. Chama o PedidoService para enviar ao backend
    this.pedidoService.createPedido(novoPedido).subscribe({
      next: (pedidoSalvo) => {
        console.log('Pedido criado com sucesso!', pedidoSalvo);
        this.successMessage = `Pedido #${pedidoSalvo.id} realizado com sucesso! Em breve sairá para entrega.`;
        this.isLoading = false;
        this.cartService.clearCart(); // Limpa o carrinho
        this.customerForm.reset();

        // (No futuro, podemos navegar para uma tela de "Obrigado" ou "Rastreamento")
        // setTimeout(() => this.router.navigate(['/']), 5000);
      },
      error: (err) => {
        console.error('Erro ao criar pedido:', err);
        this.error = 'Não foi possível finalizar o pedido. Tente novamente.';
        this.isLoading = false;
      }
    });
  }

  // --- Funções Auxiliares para o HTML ---
  isFieldInvalid(fieldName: string): boolean {
    const control = this.customerForm.get(fieldName);
    return control ? control.invalid && (control.dirty || control.touched) : false;
  }
}
<!-- checkout.component.html -->
<main class="min-h-screen bg-gray-50">
  <div class="max-w-6xl mx-auto px-4 md:px-6 lg:px-8 py-8">

    <!-- Header -->
    <section class="text-center mb-8">
      <h1 class="text-3xl md:text-4xl font-extrabold tracking-tight text-gray-900">
        Carrinho
      </h1>
      <p class="mt-2 text-gray-500">
        Revise seus itens e finalize o pedido
      </p>
    </section>

    <div class="grid grid-cols-1 lg:grid-cols-2 gap-6">
      <!-- Bloco: Carrinho -->
      <section class="bg-white rounded-xl shadow-sm ring-1 ring-black/5 overflow-hidden">
        <header class="bg-orange-500 text-white px-4 py-3 font-semibold">
          Carrinho
        </header>

        <div class="p-0">
          <!-- Tabela -->
          <div class="overflow-x-auto">
            <table class="w-full text-sm">
              <thead>
                <tr class="text-left text-gray-700">
                  <th class="px-4 py-3 w-1/2">Nome</th>
                  <th class="px-4 py-3 w-1/6">Preço</th>
                  <th class="px-4 py-3 w-1/3">Ação</th>
                </tr>
              </thead>
              <tbody>
                <tr *ngFor="let item of cartItems; trackBy: trackByItem"
                    class="even:bg-yellow-100/70">
                  <td class="px-4 py-3">
                    <span class="font-medium">{{ item.quantity }}x {{ item.name }}</span>
                  </td>
                  <td class="px-4 py-3">
                    {{ item.total | currency:'BRL' }}
                  </td>
                  <td class="px-4 py-3">
                    <div class="inline-flex items-center gap-2">
                      <button (click)="decrease(item)"
                              class="h-8 w-8 rounded-full ring-1 ring-gray-300 grid place-items-center hover:bg-gray-50"
                              aria-label="Diminuir">
                        –
                      </button>

                      <span class="inline-flex h-8 min-w-[2.25rem] items-center justify-center rounded-md ring-1 ring-gray-300 px-2 text-sm font-semibold">
                        {{ item.quantity }}
                      </span>

                      <button (click)="increase(item)"
                              class="h-8 w-8 rounded-full ring-1 ring-gray-300 grid place-items-center hover:bg-gray-50"
                              aria-label="Aumentar">
                        +
                      </button>

                      <button (click)="remove(item)"
                              class="ml-2 rounded-md px-3 py-1.5 text-sm font-semibold bg-orange-100 text-orange-700 hover:bg-orange-200">
                        Remover
                      </button>
                    </div>
                  </td>
                </tr>

                <tr *ngIf="!cartItems || cartItems.length === 0">
                  <td colspan="3" class="px-4 py-6 text-center text-gray-500">
                    Seu carrinho está vazio.
                  </td>
                </tr>
              </tbody>
            </table>
          </div>

          <!-- Total -->
          <div class="px-4 py-4 flex items-center justify-end text-sm">
            <span class="font-semibold">Total: {{ cartTotal | currency:'BRL' }}</span>
          </div>
        </div>
      </section>

      <!-- Bloco: Fazer Pedido -->
      <section class="bg-white rounded-xl shadow-sm ring-1 ring-black/5 overflow-hidden">
        <header class="bg-orange-500 text-white px-4 py-3 font-semibold">
          Fazer Pedido
        </header>

        <form class="p-4 space-y-4" (ngSubmit)="submitOrder()" [formGroup]="orderForm">
          <div>
            <label class="block text-sm font-medium text-gray-700 mb-1">Cliente</label>
            <input formControlName="clientName" type="text" placeholder="Nome do cliente"
                   class="w-full rounded-md ring-1 ring-gray-300 px-3 py-2 outline-none focus:ring-2 focus:ring-orange-500">
          </div>

          <div>
            <label class="block text-sm font-medium text-gray-700 mb-1">Telefone</label>
            <input formControlName="phone" type="tel" placeholder="(11) 99999-9999"
                   class="w-full rounded-md ring-1 ring-gray-300 px-3 py-2 outline-none focus:ring-2 focus:ring-orange-500">
          </div>

          <div>
            <label class="block text-sm font-medium text-gray-700 mb-1">Endereço</label>
            <input formControlName="address" type="text" placeholder="Rua, número, bairro, cidade..."
                   class="w-full rounded-md ring-1 ring-gray-300 px-3 py-2 outline-none focus:ring-2 focus:ring-orange-500">
          </div>

          <div>
            <label class="block text-sm font-medium text-gray-700 mb-1">Forma de Pagamento</label>
            <select formControlName="payment"
                    class="w-full rounded-md ring-1 ring-gray-300 px-3 py-2 outline-none focus:ring-2 focus:ring-orange-500">
              <option value="" disabled selected>Selecione a forma de pagamento</option>
              <option value="dinheiro">Dinheiro</option>
              <option value="pix">PIX</option>
              <option value="cartao">Cartão</option>
            </select>
          </div>

          <button type="submit" [disabled]="cartItems.length === 0 || orderForm.invalid"
                  class="w-full rounded-full bg-orange-500 px-5 py-2.5 text-white font-semibold shadow-sm
                         hover:bg-orange-600 disabled:opacity-60">
            Fazer Pedido
          </button>
        </form>
      </section>
    </div>
  </div>
</main>
