import { Component, OnInit, OnDestroy } from '@angular/core'; 
import { CommonModule } from '@angular/common';
import { PedidoService, PedidoCompleto } from '../../../services/pedido.service';
import { TopbarComponent } from '../topbar/topbar.component';
import { Subscription } from 'rxjs'; 

@Component({
  selector: 'app-order-tracker',
  standalone: true,
  imports: [ CommonModule, TopbarComponent ],
  templateUrl: './order-tracker.component.html',
  styleUrls: ['./order-tracker.component.css']
})
export class OrderTrackerComponent implements OnInit, OnDestroy { 

  pedidos: PedidoCompleto[] = [];
  isLoading = false;
  error: string | null = null;
  
  // --- CORRE√á√ÉO AQUI ---
  // Adicionado o '!' para o strict mode do TypeScript
  private pedidosSub!: Subscription; 

  constructor(private pedidoService: PedidoService) {}
  
  ngOnInit(): void {
    this.isLoading = true;
    this.pedidosSub = this.pedidoService.pedidos$.subscribe(data => {
      console.log('STATUS DOS PEDIDOS NO TRACKER (REAL-TIME):', data.map(p => p.status));
      this.pedidos = data.sort((a, b) => (b.id! > a.id! ? 1 : -1));
      this.isLoading = false;
    });

    this.pedidoService.loadAllPedidos();
  }

  ngOnDestroy(): void {
    if (this.pedidosSub) {
      this.pedidosSub.unsubscribe();
    }
  }

  // Fun√ß√£o formatarStatus (inalterada)
  formatarStatus(status: string | null | undefined): string {
    if (!status || typeof status !== 'string') {
      return 'Status Indefinido'; 
    }
  
    switch (status) {
      case 'RECEBIDO':
        return 'üìù Pedido Recebido';
      case 'EM_PREPARO':
        return 'üë®‚Äçüç≥ Em Preparo';
      case 'SAIU_PARA_ENTREGA':
        return 'üöö A Caminho';
      case 'ENTREGUE':
        return '‚úÖ Entregue';
      case 'PRONTO':
        return '‚úÖ Pronto';
      case 'CANCELADO':
        return '‚ùå Cancelado';
      default:
        return status.replaceAll('_', ' ');
    }
  }
  
}
