import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';

// Importa a Topbar do Admin (como definido no seu TS)
import { TopbarComponent } from '../topbarAdmin/topbarAdmin.component';

// Importa o servi√ßo e as interfaces corretas
import { PedidoService, PedidoCompleto, ItemPedidoCompleto } from '../../../services/pedido.service'; 

@Component({
  selector: 'app-kitchen-orders', // seletor no plural
  standalone: true,
  imports: [
    CommonModule,
    TopbarComponent 
  ],
  templateUrl: './kitchen-orders.component.html',
  styleUrls: ['./kitchen-orders.component.css']
})
export class KitchenOrdersComponent implements OnInit { 

  public pedidosDaCozinha: PedidoCompleto[] = [];
  public isLoading = false;
  public error: string | null = null;

  constructor(private pedidoService: PedidoService) { }

  ngOnInit(): void {
    this.carregarPedidos();
  }

  carregarPedidos(): void {
    this.isLoading = true;
    this.error = null;

    this.pedidoService.getAllPedidos().subscribe({
      next: (data) => {
        // --- AJUSTE 1: MUDAN√áA DE L√ìGICA ---
        // Agora filtra por "EM_PREPARO" E "PRONTO"
        this.pedidosDaCozinha = data
          .filter(pedido => pedido.status === 'EM_PREPARO' || pedido.status === 'PRONTO')
          .sort((a, b) => (a.id! < b.id! ? -1 : 1)); // Ordena por ID
        
        this.isLoading = false;
      },
      error: (err) => {
        console.error('Erro ao buscar pedidos da cozinha:', err);
        this.error = 'N√£o foi poss√≠vel carregar os pedidos.';
        this.isLoading = false;
      }
    });
  }

  /**
   * Chamado quando o bot√£o "Marcar como Pronto" √© clicado.
   */
  marcarComoPronto(pedido: PedidoCompleto): void {
    if (!pedido.id) return;
    pedido.isLoading = true; 

    this.pedidoService.updatePedidoStatus(pedido.id, 'PRONTO').subscribe({
      next: (pedidoAtualizado) => {
        // --- AJUSTE 2: MUDAN√áA DE L√ìGICA ---
        // SUCESSO! N√£o removemos mais o item.
        // Apenas atualizamos o status localmente e paramos o loading.
        pedido.status = 'PRONTO';
        pedido.isLoading = false;
      },
      error: (err) => {
        console.error('Erro ao atualizar status:', err);
        alert(`N√£o foi poss√≠vel atualizar o Pedido #${pedido.id}. Tente novamente.`);
        pedido.isLoading = false; 
      }
    });
  }

  /**
   * (PR√ìXIMO PASSO) Chamado quando o bot√£o "Enviar para Entrega" √© clicado.
   */
  enviarParaEntrega(pedido: PedidoCompleto): void {
    if (!pedido.id) return;
    pedido.isLoading = true; // Usa o mesmo 'isLoading'

    console.log(`Enviando pedido #${pedido.id} para entrega...`);

    // (Aqui voc√™ chamar√° o pr√≥ximo update de status)
    this.pedidoService.updatePedidoStatus(pedido.id, 'SAIU_PARA_ENTREGA').subscribe({
       next: (pedidoAtualizado) => {
         // Agora sim, removemos da lista da Cozinha/Prontos
         this.pedidosDaCozinha = this.pedidosDaCozinha.filter(p => p.id !== pedido.id);
       },
       error: (err) => {
         alert(`N√£o foi poss√≠vel enviar o Pedido #${pedido.id} para entrega.`);
         pedido.isLoading = false;
       }
    });
  }
}
