import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormBuilder, FormGroup, Validators, ReactiveFormsModule } from '@angular/forms';
import { TopbarComponent } from '../topbar/topbar.component';
import { PedidoService } from '../../../services/pedido.service';
import type { PedidoCompleto } from '../../../services/pedido.service';

@Component({
  selector: 'app-order-tracker',
  standalone: true,
  imports: [CommonModule, ReactiveFormsModule, TopbarComponent],
  templateUrl: './order-tracker.component.html',
  styleUrls: ['./order-tracker.component.css']
})
export class OrderTrackerComponent {

  searchForm: FormGroup;
  pedidoEncontrado: PedidoCompleto | null = null;
  isLoading = false;
  error: string | null = null;

  constructor(
    private fb: FormBuilder,
    private pedidoService: PedidoService
  ) {
    // Inicializa o formulário reativo
    this.searchForm = this.fb.group({
      pedidoId: ['', [Validators.required, Validators.pattern("^[0-9]*$")]]
    });
  }

  /**
   * Realiza a busca do pedido pelo ID informado no formulário.
   */
  onSearch(): void {
    if (this.searchForm.invalid) {
      this.error = "Por favor, insira um ID de pedido válido.";
      return;
    }

    const id = this.searchForm.value.pedidoId;

    this.isLoading = true;
    this.error = null;
    this.pedidoEncontrado = null;

    this.pedidoService.getPedidoById(id).subscribe({
      next: (pedido: PedidoCompleto) => {
        if (!pedido) {
          this.error = `Pedido #${id} não encontrado.`;
          this.pedidoEncontrado = null;
        } else {
          this.pedidoEncontrado = pedido;
        }
        this.isLoading = false;
      },
      error: (err: any) => {
        console.error('Erro ao buscar pedido:', err);
        this.error = `Não foi possível localizar o pedido #${id}.`;
        this.isLoading = false;
      }
    });
  }
}
<app-topbar></app-topbar>

<main class="max-w-5xl mx-auto p-5 pt-10">
  <div class="bg-white rounded-2xl shadow-lg p-8">

    <!-- ======= TÍTULO ======= -->
    <h1 class="text-3xl font-bold text-gray-800 mb-4">Acompanhe seu Pedido</h1>
    <p class="text-gray-600 mb-8">
      Insira o <span class="font-semibold">ID do seu pedido</span> (Ex: "2") que você recebeu na tela de finalização.
    </p>

    <!-- ======= FORMULÁRIO ======= -->
    <form [formGroup]="searchForm" (ngSubmit)="onSearch()" class="flex flex-col sm:flex-row gap-4">

      <input
        type="text"
        formControlName="pedidoId"
        placeholder="ID do Pedido (Ex: 2)"
        class="flex-grow w-full px-4 py-2 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-400 transition duration-200"
      >

      <button
        type="submit"
        [disabled]="isLoading || searchForm.invalid"
        class="px-6 py-2 rounded-xl text-white font-semibold transition duration-300"
        [ngClass]="{
          'bg-blue-600 hover:bg-blue-700': !isLoading && !searchForm.invalid,
          'bg-gray-400 cursor-not-allowed': isLoading || searchForm.invalid
        }"
      >
        {{ isLoading ? 'Buscando...' : 'Buscar' }}
      </button>
    </form>

    <!-- ======= FEEDBACK DE ERRO ======= -->
    <div
      *ngIf="error"
      class="mt-6 bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded-xl"
      role="alert"
    >
      {{ error }}
    </div>

    <!-- ======= RESULTADO ======= -->
    <div *ngIf="pedidoEncontrado" class="mt-10 border border-gray-200 rounded-2xl p-6 bg-gray-50">
      <h2 class="text-2xl font-semibold text-gray-800 mb-4">
        Detalhes do Pedido #{{ pedidoEncontrado.id }}
      </h2>

      <div class="space-y-3 text-gray-700">
        <div><strong>Cliente:</strong> {{ pedidoEncontrado.nomeCliente }}</div>
        <div><strong>Telefone:</strong> {{ pedidoEncontrado.telefoneCliente }}</div>
        <div><strong>Endereço:</strong> {{ pedidoEncontrado.enderecoCliente }}</div>
        <div><strong>Total:</strong> {{ pedidoEncontrado.total | currency:'BRL' }}</div>

        <div class="border-t pt-4 mt-4">
          <strong class="text-lg">Status:</strong>
          <span
            class="text-lg font-bold ml-2"
            [ngClass]="{
              'text-blue-600': pedidoEncontrado.status === 'RECEBIDO',
              'text-yellow-600': pedidoEncontrado.status === 'EM_PREPARO',
              'text-green-600': pedidoEncontrado.status === 'ENTREGUE' || pedidoEncontrado.status === 'SAIU_PARA_ENTREGA',
              'text-red-600': pedidoEncontrado.status === 'CANCELADO'
            }"
          >
            {{ pedidoEncontrado.status }}
          </span>
        </div>
      </div>
    </div>

  </div>
</main>
