import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterLink } from '@angular/router';
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';

// NOSSOS SERVIÇOS
import { Dish, DishService } from '../../services/dish.service';
import { CartService, CartItem } from '../../services/cart.service';

@Component({
  selector: 'app-dish-list',
  standalone: true,
  imports: [
    CommonModule,
    RouterLink
  ],
  templateUrl: './dish-list.component.html',
  styleUrls: ['./dish-list.component.css']
})
export class DishListComponent implements OnInit {

  dishes: Dish[] = [];
  isLoading = true;
  error: string | null = null;

  public totalItensNoCarrinho$: Observable<number>;
  public totalPrecoCarrinho$: Observable<number>;

  constructor(
    private dishService: DishService,
    private cartService: CartService
  ) {
    this.totalPrecoCarrinho$ = this.cartService.total$;
    this.totalItensNoCarrinho$ = this.cartService.items$.pipe(
      map((items: CartItem[]) => items.reduce((total: number, item: CartItem) => total + item.quantidade, 0))
    );
  }

  ngOnInit(): void {
    this.loadDishes();
  }

  loadDishes(): void {
    this.isLoading = true;
    this.error = null;
    this.dishService.getDishes().subscribe({
      next: (data) => {
        this.dishes = data;
        this.isLoading = false;
        console.log('Pratos carregados:', data);
      },
      error: (err) => {
        console.error('Erro ao buscar pratos:', err);
        this.error = 'Não foi possível carregar o cardápio. O backend está rodando?';
        this.isLoading = false;
      }
    });
  }

  adicionarAoCarrinho(dish: Dish): void {
    console.log('Adicionando ao carrinho:', dish.name);
    this.cartService.addItem(dish);
  }

  // REMOVIDO: O método deleteDish() foi removido daqui.
}
<!-- dish-list.component.html -->
<main class="min-h-screen bg-gray-50">
  <div class="max-w-6xl mx-auto px-4 md:px-6 lg:px-8 py-8">

    <!-- HEADER -->
    <section class="text-center mb-8">
      <h1 class="text-3xl md:text-4xl font-extrabold tracking-tight text-gray-900">
        Cardápio
      </h1>
      <p class="mt-2 text-gray-500">
        Escolha seus pratos favoritos
      </p>
    </section>

    <!-- ESTADO: carregando -->
    <section *ngIf="isLoading" class="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
      <div *ngFor="let _ of [1,2,3]" class="bg-white rounded-xl shadow-md ring-1 ring-black/5 overflow-hidden">
        <div class="h-40 w-full bg-gray-200 animate-pulse"></div>
        <div class="p-5 space-y-3">
          <div class="h-5 bg-gray-200 rounded w-2/3 animate-pulse"></div>
          <div class="h-4 bg-gray-200 rounded w-full animate-pulse"></div>
          <div class="h-4 bg-gray-200 rounded w-3/5 animate-pulse"></div>
          <div class="h-9 bg-gray-200 rounded-full w-full animate-pulse"></div>
        </div>
      </div>
    </section>

    <!-- ESTADO: erro -->
    <section *ngIf="!isLoading && error" class="max-w-lg mx-auto">
      <div class="bg-red-50 border border-red-200 text-red-700 rounded-lg p-4">
        Ocorreu um erro ao carregar o cardápio. Tente novamente.
      </div>
    </section>

    <!-- ESTADO: vazio -->
    <section *ngIf="!isLoading && !error && (!dishes || dishes.length === 0)" class="max-w-lg mx-auto">
      <div class="bg-white border border-gray-200 text-gray-700 rounded-lg p-6 text-center">
        Nenhum prato disponível no momento.
      </div>
    </section>

    <!-- GRID DE PRATOS -->
    <section *ngIf="!isLoading && !error && dishes && dishes.length > 0"
             class="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">

      <article *ngFor="let dish of dishes"
               class="bg-white rounded-xl shadow-md overflow-hidden ring-1 ring-black/5 transition
                      hover:shadow-lg hover:-translate-y-[2px] flex flex-col">

        <!-- Banner (imagem do prato OU faixa amarela com ícone) -->
        <ng-container *ngIf="dish.imageUrl; else faixaAmarela">
          <img
            [src]="dish.imageUrl"
            (error)="$any($event.target).src='https://placehold.co/600x240/EAB308/1C1917?text=Seu+Prato'"
            alt="{{ dish.name }}"
            class="h-40 w-full object-cover">
        </ng-container>

        <ng-template #faixaAmarela>
          <div class="h-40 w-full bg-yellow-400 flex items-center justify-center">
            <!-- ícone simples (talher/prato) -->
            <svg viewBox="0 0 24 24" class="w-14 h-14 text-gray-800/90" fill="currentColor" aria-hidden="true">
              <path d="M7 2a1 1 0 0 1 1 1v6a2 2 0 1 1-2 0V3a1 1 0 1 1 2 0zm4.5 2a.5.5 0 0 0-1 0v5.05A5.5 5.5 0 1 0 17 14V8.5a.5.5 0 0 0-1 0V14a4.5 4.5 0 1 1-4.5-4.5V4zM18 3a1 1 0 0 1 1 1v3h1a1 1 0 1 1 0 2h-1v7a1 1 0 1 1-2 0V4a1 1 0 0 1 1-1z"/>
            </svg>
          </div>
        </ng-template>

        <!-- Corpo -->
        <div class="p-5 flex flex-col flex-1">
          <h3 class="text-xl font-bold text-gray-900">{{ dish.name }}</h3>
          <p class="mt-1 text-gray-600 flex-1">{{ dish.description }}</p>

          <div class="mt-3">
            <span class="block text-sm text-emerald-600 font-semibold">
              {{ dish.price | currency:'BRL' }}
            </span>
          </div>

          <button (click)="adicionarAoCarrinho(dish)"
                  class="mt-4 w-full rounded-full bg-orange-500 px-5 py-2.5 text-white font-semibold
                         shadow-sm hover:bg-orange-600 active:scale-[.99] transition">
            Adicionar ao Carrinho
          </button>
        </div>
      </article>
    </section>

  </div>
</main>
