import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormBuilder, FormGroup, Validators, ReactiveFormsModule } from '@angular/forms'; // Módulos de formulário
import { ActivatedRoute, Router } from '@angular/router'; // Para navegar e ler o ID da URL
import { Dish, DishService } from '../../services/dish.service'; // O serviço que já existe

@Component({
  selector: 'app-dish-form',
  standalone: true,
  imports: [
    CommonModule,
    ReactiveFormsModule // Importante para [formGroup]
  ],
  templateUrl: './dish-form.component.html',
  styleUrls: ['./dish-form.component.css']
})
export class DishFormComponent implements OnInit {

  dishForm: FormGroup;
  isEditMode = false;
  currentDishId: number | null = null;
  isLoading = false;
  error: string | null = null;
  successMessage: string | null = null;

  constructor(
    private fb: FormBuilder,
    private dishService: DishService,
    private router: Router, // Para nos levar de volta ao cardápio
    private route: ActivatedRoute // Para ler o ID da URL (ex: /edit-dish/5)
  ) {
    // Inicializa o formulário
    this.dishForm = this.fb.group({
      name: ['', Validators.required], // Campo 'name' é obrigatório
      description: ['', Validators.required], // Campo 'description' é obrigatório
      price: [null, [Validators.required, Validators.min(0.01)]] // 'price' é obrigatório e > 0
    });
  }

  ngOnInit(): void {
    // Verifica se a URL tem um 'id'. Se tiver, estamos no modo de "Editar".
    this.route.paramMap.subscribe(params => {
      const idParam = params.get('id');
      if (idParam) {
        this.isEditMode = true;
        this.currentDishId = +idParam; // Converte string 'id' para número
        this.loadDishForEditing(this.currentDishId);
      }
    });
  }

  // Se for modo de edição, busca os dados do prato na API
  loadDishForEditing(id: number): void {
    this.isLoading = true;
    this.dishService.getDish(id).subscribe({
      next: (dish) => {
        // Preenche o formulário com os dados do prato
        this.dishForm.patchValue(dish);
        this.isLoading = false;
      },
      error: (err) => {
        this.error = 'Não foi possível carregar o prato para edição.';
        this.isLoading = false;
      }
    });
  }

  // Chamado quando o formulário é submetido
  onSubmit(): void {
    if (this.dishForm.invalid) {
      this.dishForm.markAllAsTouched(); // Mostra erros se o formulário for inválido
      return;
    }

    this.isLoading = true;
    this.error = null;
    this.successMessage = null;
    const dishData = this.dishForm.value;

    if (this.isEditMode && this.currentDishId) {
      // --- Lógica de ATUALIZAR (Editar) ---
      this.dishService.updateDish(this.currentDishId, dishData).subscribe({
        next: () => {
          this.successMessage = 'Prato atualizado com sucesso!';
          this.isLoading = false;
          // Volta para o cardápio após 2 segundos
          setTimeout(() => this.router.navigate(['/']), 2000);
        },
        error: (err) => {
          this.error = 'Erro ao atualizar o prato.';
          this.isLoading = false;
        }
      });
    } else {
      // --- Lógica de CRIAR (Adicionar) ---
      this.dishService.createDish(dishData).subscribe({
        next: () => {
          this.successMessage = 'Prato criado com sucesso!';
          this.isLoading = false;
          // Volta para o cardápio após 2 segundos
          setTimeout(() => this.router.navigate(['/']), 2000);
        },
        error: (err) => {
          this.error = 'Erro ao criar o prato.';
          this.isLoading = false;
        }
      });
    }
  }

  // --- Funções Auxiliares para mostrar erros no HTML ---
  isFieldInvalid(fieldName: string): boolean {
    const control = this.dishForm.get(fieldName);
    return control ? control.invalid && (control.dirty || control.touched) : false;
  }

  getErrorMessage(fieldName: string): string {
    const control = this.dishForm.get(fieldName);
    if (control?.errors?.['required']) {
      return 'Este campo é obrigatório.';
    }
    if (control?.errors?.['min']) {
      return 'O preço deve ser maior que zero.';
    }
    return 'Campo inválido.';
  }
}
<!-- Cabeçalho (AGORA SÓ COM LINKS DE CLIENTE) -->
<header class="bg-white shadow-md p-4">
  <div class="container mx-auto flex justify-between items-center">
    <a [routerLink]="['/']" class="text-2xl font-bold text-blue-600">iBeus Delivery</a>
    <nav class="flex gap-4">
      <!-- MANTIDO: Cliente pode rastrear pedido -->
      <a [routerLink]="['/tracker']" class="text-gray-600 hover:text-blue-600 transition duration-300">Acompanhar Pedido</a>

      <!-- REMOVIDO: Link para Adicionar Prato -->
      <!-- REMOVIDO: Link para Painel da Cozinha -->

      <!-- ADICIONADO: Link para "Modo Funcionário" -->
      <a [routerLink]="['/admin']" class="text-sm bg-gray-100 hover:bg-gray-200 text-gray-700 font-medium py-2 px-3 rounded-lg transition duration-300">
        Modo Funcionário
      </a>
    </nav>
  </div>
</header>

<!-- Conteúdo Principal -->
<div class="container mx-auto p-6">

  <!-- Feedback (Carregando, Erro, Vazio) - (O mesmo de antes) -->
  <div *ngIf="isLoading" class="text-center py-20">...</div>
  <div *ngIf="error" class="bg-red-100 ...">{{ error }}</div>
  <div *ngIf="!isLoading && !error && dishes.length === 0" class="text-center py-20 ...">
    <h2 class="text-3xl font-semibold ...">Cardápio Vazio</h2>
    <p class="text-gray-500 mb-6">Ainda não há pratos cadastrados.</p>
    <!-- REMOVIDO: Botão "Adicionar o primeiro prato" -->
  </div>

  <!-- ########## GRELHA (GRID) DE PRATOS - VERSÃO CLIENTE ########## -->
  <div *ngIf="!isLoading && !error && dishes.length > 0" class="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">

    <div *ngFor="let dish of dishes" class="bg-white rounded-xl shadow-lg overflow-hidden transition-transform duration-300 hover:shadow-2xl hover:-translate-y-1 flex flex-col">

      <img class="h-56 w-full object-cover"
           [src]="dish.imageUrl ? dish.imageUrl : 'https://placehold.co/600x400/E2E8F0/94A3B8?text=Sem+Imagem'"
           onerror="this.src='https://placehold.co/600x400/E2E8F0/94A3B8?text=Sem+Imagem'"
           alt="{{ dish.name }}">

      <div class="p-6 flex flex-col flex-grow">
        <h3 class="text-2xl font-bold text-gray-900 mb-2">{{ dish.name }}</h3>
        <p class="text-gray-600 mb-4 flex-grow">{{ dish.description }}</p>
        <span class="text-3xl font-bold text-green-600 mb-5">{{ dish.price | currency:'BRL' }}</span>

        <div class="mt-auto">
          <!-- MANTIDO: Cliente pode adicionar ao carrinho -->
          <button (click)="adicionarAoCarrinho(dish)" class="w-full bg-blue-600 hover:bg-blue-700 text-white font-bold py-3 px-4 rounded-lg shadow-md transition duration-300 mb-3">
            Adicionar ao Carrinho
          </button>

          <!-- REMOVIDO: Botões de Admin (Editar/Deletar) -->
        </div>
      </div>
    </div>

  </div>
</div>


<!-- ########## BARRA DE CHECKOUT (A mesma de antes) ########## -->
<ng-container *ngIf="(totalItensNoCarrinho$ | async) as totalItens">
  <div *ngIf="totalItens > 0"
       class="fixed bottom-0 left-0 right-0 bg-gradient-to-r from-blue-600 to-blue-800 text-white p-4 shadow-2xl-top animate-slide-up">
    <div class="container mx-auto flex justify-between items-center">
      <div>
        <span class="font-bold text-xl">{{ totalItens }} {{ totalItens === 1 ? 'item' : 'itens' }} no carrinho</span>
        <span class="ml-4 text-lg">{{ (totalPrecoCarrinho$ | async) | currency:'BRL' }}</span>
      </div>
      <a [routerLink]="['/checkout']" class="bg-white text-blue-700 font-bold py-2 px-6 rounded-lg shadow-md hover:bg-gray-100 transition duration-300">
        Finalizar Pedido
      </a>
    </div>
  </div>
</ng-container>

<!-- Estilos (O mesmo de antes) -->
<style>
  @keyframes slide-up {
    from { transform: translateY(100%); opacity: 0; }
    to   { transform: translateY(0);    opacity: 1; }
  }

  .animate-slide-up {
    animation: slide-up 0.3s ease-out;
  }

  .shadow-2xl-top {
    box-shadow: 0 -12px 24px rgba(2, 6, 23, 0.12);
  }
</style>

<!-- Rodapé Simples -->
<footer class="bg-gray-800 text-white py-6 mt-12">
  <div class="container mx-auto text-center">
    <p>&copy; 2024 iBeus Delivery. Todos os direitos reservados.</p>
  </div>
</footer>
