// src/app/components/dish-form/dish-form.component.ts

import { Component, OnInit } from '@angular/core';
import { FormsModule } from '@angular/forms';
import { DishService, Dish } from '../../services/dish.service';
import { ActivatedRoute, Router } from '@angular/router';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-dish-form',
  templateUrl: './dish-form.component.html',
  styleUrls: ['./dish-form.component.css'],
  standalone: true,
  imports: [FormsModule, CommonModule]
})
export class DishFormComponent implements OnInit {
  dish: Dish = {
    name: '',
    description: '',
    price: 0
  };
  isEdit: boolean = false;

  constructor(
    private dishService: DishService,
    private route: ActivatedRoute,
    private router: Router
  ) {}

  ngOnInit(): void {
    const id = this.route.snapshot.paramMap.get('id');
    if (id) {
      this.isEdit = true;
      this.dishService.getDish(+id).subscribe((data: Dish) => {
        this.dish = data;
      });
    }
  }

  saveDish() {
    if (this.isEdit) {
      this.dishService.updateDish(this.dish.id!, this.dish).subscribe(() => {
        this.router.navigate(['/']);
      });
    } else {
      this.dishService.createDish(this.dish).subscribe(() => {
        this.router.navigate(['/']);
      });
    }
  }
}
<!-- src/app/components/dish-form/dish-form.component.html -->

<h2>{{ isEdit ? 'Edit Dish' : 'Add Dish' }}</h2>
<form (ngSubmit)="saveDish()">
  <label for="name">Name:</label>
  <input type="text" id="name" [(ngModel)]="dish.name" name="name" required>

  <label for="description">Description:</label>
  <input type="text" id="description" [(ngModel)]="dish.description" name="description" required>

  <label for="price">Price:</label>
  <input type="number" id="price" [(ngModel)]="dish.price" name="price" required>

  <button type="submit">{{ isEdit ? 'Update' : 'Add' }}</button>
</form>
