import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterLink } from '@angular/router';
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';

// NOSSOS SERVI√áOS
import { Dish, DishService } from '../../services/dish.service';
import { CartService, CartItem } from '../../services/cart.service';

@Component({
  selector: 'app-dish-list',
  standalone: true,
  imports: [
    CommonModule,
    RouterLink
  ],
  templateUrl: './dish-list.component.html',
  styleUrls: ['./dish-list.component.css']
})
export class DishListComponent implements OnInit {

  dishes: Dish[] = [];
  isLoading = true;
  error: string | null = null;

  public totalItensNoCarrinho$: Observable<number>;
  public totalPrecoCarrinho$: Observable<number>;

  constructor(
    private dishService: DishService,
    private cartService: CartService
  ) {
    this.totalPrecoCarrinho$ = this.cartService.total$;
    this.totalItensNoCarrinho$ = this.cartService.items$.pipe(
      map((items: CartItem[]) => items.reduce((total: number, item: CartItem) => total + item.quantidade, 0))
    );
  }

  ngOnInit(): void {
    this.loadDishes();
  }

  loadDishes(): void {
    this.isLoading = true;
    this.error = null;
    this.dishService.getDishes().subscribe({
      next: (data) => {
        this.dishes = data;
        this.isLoading = false;
        console.log('Pratos carregados:', data);
      },
      error: (err) => {
        console.error('Erro ao buscar pratos:', err);
        this.error = 'N√£o foi poss√≠vel carregar o card√°pio. O backend est√° rodando?';
        this.isLoading = false;
      }
    });
  }

  adicionarAoCarrinho(dish: Dish): void {
    console.log('Adicionando ao carrinho:', dish.name);
    this.cartService.addItem(dish);
  }

  // REMOVIDO: O m√©todo deleteDish() foi removido daqui.
}
<header class="w-full bg-[var(--brand)] text-white">
  <div class="max-w-6xl mx-auto px-4 sm:px-6 py-3 flex items-center justify-between">
    <span class="font-semibold">Bem-vindo! üßæ</span>

    <nav class="flex gap-2">
      <a routerLink="/cardapio"
         class="px-4 py-2 rounded-xl bg-white/10 hover:bg-white/20 transition">Card√°pio</a>

      <a routerLink="/carrinho"
         class="relative px-4 py-2 rounded-xl bg-white text-[var(--brand)] hover:bg-white/90 transition">
        Carrinho
        <span *ngIf="cartCount>0"
              class="absolute -top-2 -right-2 text-xs font-bold bg-[var(--accent)] text-black rounded-full px-2 py-0.5">
          {{ cartCount }}
        </span>
      </a>

      <a routerLink="/meus-pedidos"
         class="px-4 py-2 rounded-xl bg-white/10 hover:bg-white/20 transition">Meus Pedidos</a>

      <button class="px-4 py-2 rounded-xl bg-white/10 hover:bg-white/20 transition">Sair</button>
    </nav>
  </div>
</header>
<header class="w-full bg-[var(--brand)] text-white">
  <div class="max-w-6xl mx-auto px-4 sm:px-6 py-3 flex items-center justify-between">
    <span class="font-semibold">Bem-vindo! üßæ</span>

    <nav class="flex gap-2">
      <a routerLink="/cardapio"
         class="px-4 py-2 rounded-xl bg-white/10 hover:bg-white/20 transition">Card√°pio</a>

      <a routerLink="/carrinho"
         class="relative px-4 py-2 rounded-xl bg-white text-[var(--brand)] hover:bg-white/90 transition">
        Carrinho
        <span *ngIf="cartCount>0"
              class="absolute -top-2 -right-2 text-xs font-bold bg-[var(--accent)] text-black rounded-full px-2 py-0.5">
          {{ cartCount }}
        </span>
      </a>

      <a routerLink="/meus-pedidos"
         class="px-4 py-2 rounded-xl bg-white/10 hover:bg-white/20 transition">Meus Pedidos</a>

      <button class="px-4 py-2 rounded-xl bg-white/10 hover:bg-white/20 transition">Sair</button>
    </nav>
  </div>
</header>
<section class="max-w-6xl mx-auto px-4 sm:px-6 pt-8">
  <h1 class="text-2xl sm:text-3xl font-bold text-center text-ink">Card√°pio</h1>
  <p class="text-center text-gray-500 mt-1">Escolha seus pratos favoritos</p>

  <div class="grid gap-6 sm:gap-8 mt-8 grid-cols-1 sm:grid-cols-2 lg:grid-cols-3">
    <article *ngFor="let dish of dishes"
             class="rounded-2xl overflow-hidden shadow-[0_10px_24px_-12px_rgba(0,0,0,.2)] bg-white border border-gray-100">
      <div class="h-36 accent flex items-center justify-center">
        <img src="assets/plate.svg" alt="" class="w-14 h-14 opacity-90">
      </div>

      <div class="p-4">
        <h3 class="text-lg font-semibold text-ink">{{ dish.name }}</h3>
        <p class="text-gray-500 text-sm mt-1">{{ dish.description }}</p>

        <div class="flex items-center justify-between mt-4">
          <span class="font-bold text-ink">R$ {{ dish.price | number:'1.2-2' }}</span>
          <button (click)="openAddModal(dish)"
                  class="px-4 py-2 rounded-xl bg-[var(--brand)] hover:bg-[var(--brand-700)] text-white transition">
            Adicionar ao Carrinho
          </button>
        </div>
      </div>
    </article>
  </div>
</section>
<!-- overlay -->
<div *ngIf="modalOpen" class="fixed inset-0 bg-black/40 flex items-center justify-center z-50">
  <!-- content -->
  <div class="w-[90vw] max-w-lg bg-white rounded-2xl shadow-xl overflow-hidden">
    <div class="px-5 py-3 bg-[var(--brand)] text-white flex items-center justify-between">
      <h4 class="font-semibold">Adicionar ao Carrinho</h4>
      <button (click)="closeModal()" class="opacity-90 hover:opacity-100">‚úï</button>
    </div>

    <div class="p-5 space-y-4">
      <div>
        <h5 class="text-lg font-semibold text-ink">{{ selectedDish?.name }}</h5>
        <p class="text-gray-500">{{ selectedDish?.description }}</p>
        <p class="mt-1 font-medium">R$ {{ selectedDish?.price | number:'1.2-2' }}</p>
      </div>

      <!-- stepper -->
      <div class="flex items-center gap-3">
        <button (click)="dec()" class="w-10 h-10 rounded-xl bg-gray-100 hover:bg-gray-200">‚àí</button>
        <input type="number" [value]="qty" class="w-14 h-10 text-center border rounded-xl">
        <button (click)="inc()" class="w-10 h-10 rounded-xl bg-gray-100 hover:bg-gray-200">+</button>
      </div>

      <div class="rounded-xl bg-[var(--accent)] text-black px-4 py-3 font-semibold text-center">
        {{ qty }}x {{ selectedDish?.name }} = R$ {{ (selectedDish?.price * qty) | number:'1.2-2' }}
      </div>

      <div class="flex justify-end gap-3 pt-1">
        <button (click)="closeModal()" class="px-4 py-2 rounded-xl bg-gray-100 hover:bg-gray-200">Cancelar</button>
        <button (click)="addToCart()" class="px-4 py-2 rounded-xl bg-[var(--brand)] text-white hover:bg-[var(--brand-700)]">
          Adicionar ao Carrinho
        </button>
      </div>
    </div>
  </div>
</div>
