import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterLink } from '@angular/router';
import { TopbarComponent } from '../../admin-area/topbarAdmin/topbarAdmin.component';
import { FormBuilder, FormGroup, Validators, ReactiveFormsModule } from '@angular/forms';
import { PedidoService, PedidoCompleto } from '../../../services/pedido.service';
import { Dish, DishService } from '../../../services/dish.service'; 

@Component({
  selector: 'app-admin-dashboard',
  standalone: true,
  imports: [CommonModule, RouterLink, TopbarComponent, ReactiveFormsModule],
  templateUrl: './admin-dashboard.component.html',
  styleUrls: ['./admin-dashboard.component.css']
})
export class AdminDashboardComponent implements OnInit {

  // Seção de Pedidos
  pedidos: PedidoCompleto[] = [];
  isLoadingPedidos = true;
  errorPedidos: string | null = null;
  statusList: string[] = [
    'RECEBIDO', 'EM_PREPARO', 'SAIU_PARA_ENTREGA', 'ENTREGUE', 'CANCELADO'
  ];
  
  dishForm: FormGroup;
  dishFormIsLoading = false;
  dishFormError: string | null = null;
  dishFormSuccessMessage: string | null = null;

  // NOVA SEÇÃO DE PRATOS
  dishes: Dish[] = [];
  isLoadingDishes = true;
  errorDishes: string | null = null;

  // INJETA OS DOIS SERVIÇOS
  constructor(
    private pedidoService: PedidoService,
    private dishService: DishService, // <-- INJETA O DISH SERVICE
    private fb: FormBuilder // Injetado para construir o formulário
  ) {
    // Inicializa o formulário de pratos
    this.dishForm = this.fb.group({
      name: ['', Validators.required],
      description: ['', Validators.required],
      price: [null, [Validators.required, Validators.min(0.01)]]
    });
  }

  ngOnInit(): void {
    // INICIA O CARREGAMENTO E SE INSCREVE NAS LISTAS
    this.loadPedidos();
    this.loadDishes();
  }

  loadPedidos(): void {
    this.isLoadingPedidos = true;
    // 1. Se inscreve no Observable de pedidos do serviço
    this.pedidoService.pedidos$.subscribe({
      next: (data) => {
        // Ordena os dados recebidos e atualiza a propriedade do componente
        this.pedidos = data.sort((a, b) => b.id - a.id);
        this.isLoadingPedidos = false;
        this.errorPedidos = null;
      },
      error: (err) => {
        this.errorPedidos = 'Não foi possível carregar os pedidos.';
        this.isLoadingPedidos = false;
      }
    });
    // 2. Dispara o carregamento inicial dos dados
    this.pedidoService.loadAllPedidos();
  }

  // CARREGAR PRATOS
  loadDishes(): void {
    this.isLoadingDishes = true;
    this.errorDishes = null;
    this.dishService.getDishes().subscribe({
      next: (data) => {
        this.dishes = data.sort((a, b) => (a.id || 0) - (b.id || 0));
        this.isLoadingDishes = false;
      },
      error: (err) => {
        this.errorDishes = 'Não foi possível carregar os pratos.';
        this.isLoadingDishes = false;
      }
    });
  }

  //DELETAR PRATOS
  deleteDish(id: number | undefined): void {
    if (!id) return;
    if (confirm('Tem certeza que deseja deletar este prato?')) {
      this.dishService.deleteDish(id).subscribe({
        next: () => {
          this.loadDishes();

        },
        error: (err) => {
          alert('Erro ao deletar o prato.');
        }
      });
    }
  }

  onDishFormSubmit(): void {
    if (this.dishForm.invalid) {
      this.dishForm.markAllAsTouched();
      return;
    }

    this.dishFormIsLoading = true;
    this.dishFormError = null;
    this.dishFormSuccessMessage = null;

    this.dishService.createDish(this.dishForm.value).subscribe({
      next: () => {
        this.dishFormSuccessMessage = 'Prato criado com sucesso!';
        this.dishFormIsLoading = false;
        this.loadDishes(); // Atualiza a lista de pratos na tela
        this.dishForm.reset(); // Limpa o formulário
        // Limpa a mensagem de sucesso após 3 segundos
        setTimeout(() => this.dishFormSuccessMessage = null, 3000);
      },
      error: (err) => {
        this.dishFormError = 'Erro ao criar o prato.';
        this.dishFormIsLoading = false;
      }
    });
  }

  isDishFieldInvalid(fieldName: string): boolean {
    const control = this.dishForm.get(fieldName);
    return !!control && control.invalid && (control.dirty || control.touched);
  }

  getDishErrorMessage(fieldName: string): string {
    const control = this.dishForm.get(fieldName);
    if (control?.errors?.['required']) { return 'Este campo é obrigatório.'; }
    if (control?.errors?.['min']) { return 'O preço deve ser maior que zero.'; }
    return 'Campo inválido.';
  }


  onStatusChange(pedidoId: number, event: Event): void {
    const selectElement = event.target as HTMLSelectElement;
    const novoStatus = selectElement.value;
    if (!novoStatus) return;

    this.pedidoService.updatePedidoStatus(pedidoId, novoStatus).subscribe({
      next: () => {
        // A lista será atualizada automaticamente pelo BehaviorSubject no serviço.
      },
      error: (err) => {
        alert('Erro ao atualizar status. (O bug do @Transactional ainda pode estar aqui se o Eclipse não reiniciou corretamente!)');
      }
    });
  }
}
<app-topbar></app-topbar>
  
  <div class="container mx-auto p-6 max-w-7xl">

  <!-- Cabeçalho do Admin -->
  <div class="flex justify-between items-center mb-8">
    <h1 class="text-3xl font-bold text-gray-800">Painel do Funcionário</h1>
    <a [routerLink]="['/']" class="bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-lg shadow-md transition duration-300">
      Ir para o Site
    </a>
  </div>


 <!-- ########## SEÇÃO 1: GERENCIAMENTO DE PRATOS ########## -->
 <div class="bg-white rounded-xl shadow-lg p-6 mb-10">
  <div class="flex justify-between items-center mb-4">
    <h2 class="text-2xl font-semibold text-gray-800">Gerenciamento de Pratos (Cardápio)</h2>
  </div>

  <!-- Tabela de Pratos (Nova) -->
  <div *ngIf="isLoadingDishes" class="text-center py-5 text-gray-600">Carregando pratos...</div>
  <div *ngIf="errorDishes" class="bg-red-100 ...">{{ errorDishes }}</div>
  <div *ngIf="!isLoadingDishes && !errorDishes && dishes.length === 0" class="text-center py-10 ...">
    <h2 class="text-xl ...">Nenhum Prato Cadastrado</h2>
  </div>

  <div *ngIf="!isLoadingDishes && !errorDishes && dishes.length > 0" class="overflow-x-auto">
    <table class="min-w-full divide-y divide-gray-200">
      <thead class="text-white" style="background-color: #FF6347;">
        <tr>
          <th class="px-6 py-2 text-left text-xs font-medium uppercase tracking-wider">ID</th>
          <th class="px-6 py-2 text-left text-xs font-medium uppercase tracking-wider">Prato</th>
          <th class="px-6 py-2 text-left text-xs font-medium uppercase tracking-wider">Descrição</th>
          <th class="px-6 py-2 text-left text-xs font-medium uppercase tracking-wider">Preço</th>
          <th class="px-6 py-2 text-center text-xs font-medium uppercase tracking-wider">Ações</th>
        </tr>
      </thead>
      <tbody class="divide-y divide-gray-200">
        <tr *ngFor="let dish of dishes; let i = index" [style.background-color]="i % 2 === 0 ? '#FFD700' : '#FFF8DC'">
          <td class="px-6 py-2 text-sm text-gray-900">#{{ dish.id }}</td>
          <td class="px-6 py-2 text-sm text-gray-900">{{ dish.name }}</td>
          <td class="px-6 py-2 text-sm text-gray-900 max-w-sm truncate">{{ dish.description }}</td>
          <td class="px-6 py-2 text-sm text-gray-900">{{ dish.price | currency:'BRL' }}</td>
          <td class="px-6 py-2 text-sm text-gray-900 text-center space-x-2">
            <a [routerLink]="['/admin/edit-dish', dish.id]" class="px-4 py-2 rounded-md border border-gray-300 bg-gray-100 text-gray-800 font-medium transition hover:bg-gray-200">
              Editar
            </a>
            <button (click)="deleteDish(dish.id)" class="px-4 py-2 rounded-md border border-red-300 bg-red-100 text-gray-800 font-medium transition hover:bg-red-200">
              Deletar
            </button>
          </td>
        </tr>
      </tbody>
    </table>
  </div>
</div>


<!-- ########## SEÇÃO 2: ADICIONAR PRATO ########## -->
<div class="bg-white rounded-xl shadow-lg p-6 mb-10">
 
  <h2 class="text-2xl font-semibold text-white mb-6 p-4 rounded-lg" style="background-color: #ff6347;">
    Adicionar Novo Prato
  </h2>

  <form [formGroup]="dishForm" (ngSubmit)="onDishFormSubmit()" class="space-y-6">

        <div>
      <label for="name" class="block text-sm font-medium text-gray-700 mb-1">Nome do Prato</label>
      <input type="text" id="name" formControlName="name"
             class="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2"
             [ngClass]="{'border-red-500 focus:ring-red-300': isDishFieldInvalid('name'), 'border-gray-300 focus:ring-blue-400': !isDishFieldInvalid('name')}">
      <div *ngIf="isDishFieldInvalid('name')" class="text-red-600 text-sm mt-1">
        {{ getDishErrorMessage('name') }}
      </div>
    </div>

        <div>
      <label for="description" class="block text-sm font-medium text-gray-700 mb-1">Descrição</label>
      <textarea id="description" formControlName="description" rows="3"
                class="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2"
                [ngClass]="{'border-red-500 focus:ring-red-300': isDishFieldInvalid('description'), 'border-gray-300 focus:ring-blue-400': !isDishFieldInvalid('description')}"></textarea>
      <div *ngIf="isDishFieldInvalid('description')" class="text-red-600 text-sm mt-1">
        {{ getDishErrorMessage('description') }}
      </div>
    </div>

        <div>
      <label for="price" class="block text-sm font-medium text-gray-700 mb-1">Preço (R$)</label>
      <input type="number" id="price" formControlName="price" placeholder="Ex: 29.90"
             class="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2"
             [ngClass]="{'border-red-500 focus:ring-red-300': isDishFieldInvalid('price'), 'border-gray-300 focus:ring-blue-400': !isDishFieldInvalid('price')}">
      <div *ngIf="isDishFieldInvalid('price')" class="text-red-600 text-sm mt-1">
        {{ getDishErrorMessage('price') }}
      </div>
    </div>

        <div *ngIf="dishFormSuccessMessage" class="bg-green-100 border border-green-400 text-green-700 px-4 py-3 rounded-lg" role="alert">
      {{ dishFormSuccessMessage }}
    </div>
    <div *ngIf="dishFormError" class="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded-lg" role="alert">
      {{ dishFormError }}
    </div>

        <div class="flex justify-end gap-4 pt-4">
      <button type="button" (click)="dishForm.reset()" class="px-6 py-2 rounded-lg text-gray-700 bg-gray-100 hover:bg-gray-200 transition duration-300">
        Limpar
      </button>
      <button type="submit" [disabled]="dishForm.invalid || dishFormIsLoading"
              class="px-6 py-2 rounded-lg text-white font-semibold transition duration-300"
              [ngClass]="{'bg-blue-600 hover:bg-blue-700': !dishForm.invalid, 'bg-gray-400 cursor-not-allowed': dishForm.invalid || dishFormIsLoading}">
        {{ dishFormIsLoading ? 'Salvando...' : 'Salvar Prato' }}
      </button>
    </div>
  </form>
</div>


 <!-- ########## SEÇÃO 3: GERENCIAMENTO DE PEDIDOS ########## -->
 <div class="bg-white rounded-xl shadow-lg p-6 mb-10">
  <h2 class="text-2xl font-semibold text-gray-800 mb-4">Fila de Pedidos da Cozinha</h2>

    <div *ngIf="isLoadingPedidos" class="text-center py-5 text-gray-600">Carregando pedidos...</div>
  <div *ngIf="errorPedidos" class="bg-red-100 text-red-700 p-4 rounded-lg mb-4">{{ errorPedidos }}</div>
  <div *ngIf="!isLoadingPedidos && !errorPedidos && pedidos.length === 0" class="text-center py-10 text-gray-500">
    <h2 class="text-xl font-medium">Nenhum Pedido Encontrado</h2>
    <p>A fila de pedidos está vazia.</p>
  </div>

  <div *ngIf="!isLoadingPedidos && !errorPedidos && pedidos.length > 0" class="overflow-x-auto">
    <table class="min-w-full divide-y divide-gray-200">
                <thead class="text-white">         <tr style="background-color: #FF6347;">
          <th class="px-6 py-2 text-left text-xs font-medium uppercase tracking-wider">Cliente</th>
          <th class="px-6 py-2 text-left text-xs font-medium uppercase tracking-wider">Pagamento</th>
          <th class="px-6 py-2 text-left text-xs font-medium uppercase tracking-wider">Itens</th>
          <th class="px-6 py-2 text-left text-xs font-medium uppercase tracking-wider">Status</th>
          <th class="px-6 py-2 text-left text-xs font-medium uppercase tracking-wider">Data</th>
        </tr>
      </thead>
      <tbody class="divide-y divide-gray-200">
          <tr *ngFor="let pedido of pedidos; let i = index" 
            [style.background-color]="i % 2 === 0 ? '#FFD700' : '#FFF8DC'">
          
          <td class="px-6 py-2 text-sm text-gray-900">{{ pedido.nomeCliente }}</td>
          <td class="px-6 py-2 text-sm text-gray-900">{{ pedido.total | currency:'BRL' }}</td>
          <td class="px-6 py-2 text-sm text-gray-900">
            <ul class="list-disc list-inside">
              <li *ngFor="let item of pedido.itens">
                {{ item.quantidade }}x {{ item.dish.name }}
              </li>
            </ul>
          </td>
          <td class="px-6 py-2 text-sm text-gray-900">
            <span class="px-3 py-1 inline-flex text-xs leading-5 font-semibold rounded-full"
                  [ngClass]="{
                    'bg-blue-100 text-blue-800': pedido.status === 'RECEBIDO',
                    'bg-yellow-100 text-yellow-800': pedido.status === 'EM_PREPARO',
                    'bg-orange-100 text-orange-800': pedido.status === 'SAIU_PARA_ENTREGA',
                    'bg-green-100 text-green-800': pedido.status === 'ENTREGUE',
                    'bg-red-100 text-red-800': pedido.status === 'CANCELADO'
                  }">
              {{ pedido.status }}
            </span>
          </td>
          <td class="px-6 py-2 text-sm text-gray-900">
            {{ pedido.dataHora | date:'dd/MM/yyyy HH:mm' }}
          </td>
        </tr>
      </tbody>
    </table>
  </div>
</div>
 



</div>
