<div class="container mx-auto p-6 max-w-6xl">
  <div class="flex justify-between items-center mb-6">
    <h1 class="text-3xl font-bold text-gray-800">Painel da Cozinha</h1>
    <a [routerLink]="['/']" class="bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-lg shadow-md transition duration-300">
      Voltar ao Cardápio
    </a>
  </div>

  <!-- Feedback de Carregamento -->
  <div *ngIf="isLoading" class="text-center py-10">
    <p class="text-gray-600 text-lg">Carregando pedidos...</p>
  </div>

  <!-- Feedback de Erro -->
  <div *ngIf="error" class="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded-lg" role="alert">
    {{ error }}
  </div>

  <!-- Tabela de Pedidos -->
  <div *ngIf="!isLoading && !error && pedidos.length > 0" class="bg-white rounded-xl shadow-lg overflow-hidden">
    <table class="min-w-full divide-y divide-gray-200">
      <thead class="bg-gray-100">
        <tr>
          <th class="px-6 py-3 text-left text-xs font-medium text-gray-600 uppercase tracking-wider">ID</th>
          <th class="px-6 py-3 text-left text-xs font-medium text-gray-600 uppercase tracking-wider">Cliente</th>
          <th class="px-6 py-3 text-left text-xs font-medium text-gray-600 uppercase tracking-wider">Telefone</th>
          <th class="px-6 py-3 text-left text-xs font-medium text-gray-600 uppercase tracking-wider">Endereço</th>
          <th class="px-6 py-3 text-left text-xs font-medium text-gray-600 uppercase tracking-wider">Total</th>
          <th class="px-6 py-3 text-left text-xs font-medium text-gray-600 uppercase tracking-wider">Status</th>
        </tr>
      </thead>
      <tbody class="bg-white divide-y divide-gray-200">

        <!-- Loop *ngFor para cada 'pedido' -->
        <tr *ngFor="let pedido of pedidos" class="hover:bg-gray-50">
          <td class="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">#{{ pedido.id }}</td>
          <td class="px-6 py-4 whitespace-nowrap text-sm text-gray-700">{{ pedido.nomeCliente }}</td>
          <td class="px-6 py-4 whitespace-nowrap text-sm text-gray-700">{{ pedido.telefoneCliente }}</td>
          <td class="px-6 py-4 whitespace-nowrap text-sm text-gray-700 max-w-xs truncate">{{ pedido.enderecoCliente }}</td>
          <td class="px-6 py-4 whitespace-nowrap text-sm text-gray-700">{{ pedido.total | currency:'BRL' }}</td>
          <td class="px-6 py-4 whitespace-nowrap text-sm">
            <!-- Dropdown de Status -->
            <select (change)="onStatusChange(pedido.id, $event)"
                    class="rounded-lg border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-400"
                    [ngClass]="{
                      'bg-blue-100 text-blue-800': pedido.status === 'RECEBIDO',
                      'bg-yellow-100 text-yellow-800': pedido.status === 'EM_PREPARO',
                      'bg-green-100 text-green-800': pedido.status === 'SAIU_PARA_ENTREGA' || pedido.status === 'ENTREGUE',
                      'bg-red-100 text-red-800': pedido.status === 'CANCELADO'
                    }">
              <option *ngFor="let status of statusList" [value]="status" [selected]="status === pedido.status">
                {{ status }}
              </option>
            </select>
          </td>
        </tr>

      </tbody>
    </table>
  </div>

  <!-- Mensagem de Pedidos Vazios -->
  <div *ngIf="!isLoading && !error && pedidos.length === 0" class="text-center py-20 bg-white rounded-xl shadow-lg">
    <h2 class="text-2xl font-semibold text-gray-700 mb-4">Nenhum Pedido Encontrado</h2>
    <p class="text-gray-500">Ainda não há pedidos no sistema.</p>
  </div>
</div>
