import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormBuilder, FormGroup, Validators, ReactiveFormsModule } from '@angular/forms';
import { TopbarComponent } from '../topbar/topbar.component';
import { PedidoService } from '../../../services/pedido.service';
import type { PedidoCompleto } from '../../../services/pedido.service';

@Component({
  selector: 'app-order-tracker',
  standalone: true,
  imports: [CommonModule, ReactiveFormsModule, TopbarComponent],
  templateUrl: './order-tracker.component.html',
  styleUrls: ['./order-tracker.component.css']
})
export class OrderTrackerComponent {

  searchForm: FormGroup;
  pedidoEncontrado: PedidoCompleto | null = null;
  isLoading = false;
  error: string | null = null;

  constructor(
    private fb: FormBuilder,
    private pedidoService: PedidoService
  ) {
    // Inicializa o formulário reativo
    this.searchForm = this.fb.group({
      pedidoId: ['', [Validators.required, Validators.pattern("^[0-9]*$")]]
    });
  }

  /**
   * Realiza a busca do pedido pelo ID informado no formulário.
   */
  onSearch(): void {
    if (this.searchForm.invalid) {
      this.error = "Por favor, insira um ID de pedido válido.";
      return;
    }

    const id = this.searchForm.value.pedidoId;

    this.isLoading = true;
    this.error = null;
    this.pedidoEncontrado = null;

    this.pedidoService.getPedidoById(id).subscribe({
      next: (pedido: PedidoCompleto) => {
        if (!pedido) {
          this.error = `Pedido #${id} não encontrado.`;
          this.pedidoEncontrado = null;
        } else {
          this.pedidoEncontrado = pedido;
        }
        this.isLoading = false;
      },
      error: (err: any) => {
        console.error('Erro ao buscar pedido:', err);
        this.error = `Não foi possível localizar o pedido #${id}.`;
        this.isLoading = false;
      }
    });
  }
}
<app-topbar></app-topbar>

<main class="max-w-6xl mx-auto px-4 pt-8 pb-24">
  <section class="text-center mb-8">
    <h1 class="text-3xl text-gray-900">Meus Pedidos</h1>
    <p class="text-gray-500 mt-2">Acompanhe o status dos seus pedidos</p>
  </section>

  <div class="max-w-3xl mx-auto rounded-2xl border-2 border-gray-300 p-5 bg-white shadow-sm">
    <div class="bg-[#ff6f5e] text-white px-4 py-3 font-semibold rounded-t-2xl">
      Meus Pedidos
    </div>

    <div class="rounded-b-2xl bg-gray-50">
      
      <!-- LOADING -->
      <div *ngIf="isLoading" class="p-8 text-center text-gray-500">
        Buscando pedidos...
      </div>

      <!-- ERRO -->
      <div *ngIf="error" class="p-8 m-4 bg-red-50 text-red-700 border border-red-200 rounded-lg text-center">
        {{ error }}
      </div>

      <!-- SEM PEDIDOS -->
      <div *ngIf="!isLoading && !error && pedidos.length === 0" class="p-8 text-center text-gray-500">
        Você ainda não fez nenhum pedido.
      </div>

      <!-- LISTAGEM -->
      <div *ngFor="let pedido of pedidos"
           class="bg-white p-4 border-b border-gray-200 last:rounded-b-2xl last:border-b-0">

        <div class="flex justify-between items-center mb-3">
          <div>
            <h3 class="font-bold text-lg text-gray-900">Pedido #{{ pedido.id }}</h3>
            <p class="text-sm text-gray-500">{{ pedido.data | date:'dd/MM/yyyy às HH:mm' }}</p>
          </div>
          <div class="font-bold text-lg text-gray-800">
            {{ pedido.total | currency:'BRL' }}
          </div>
        </div>

        <!-- STATUS -->
        <div class="rounded-lg p-3 text-center font-semibold text-lg flex items-center justify-center gap-2"
             [ngClass]="{
               'bg-green-100 text-green-800': pedido.status === 'PRONTO' || pedido.status === 'ENTREGUE',
               'bg-blue-100 text-blue-800': pedido.status === 'SAIU_PARA_ENTREGA',
               'bg-yellow-100 text-yellow-800': pedido.status === 'EM_PREPARO' || pedido.status === 'RECEBIDO',
               'bg-red-100 text-red-800': pedido.status === 'CANCELADO'
             }">

          <svg *ngIf="pedido.status === 'PRONTO' || pedido.status === 'ENTREGUE'"
               class="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M5 13l4 4L19 7"></path>
          </svg>

          <svg *ngIf="pedido.status === 'SAIU_PARA_ENTREGA'"
               class="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path d="M9 17a2 2 0 11-4 0 2 2 0 014 0zM19 17a2 2 0 11-4 0 2 2 0 014 0z"></path>
            <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M13 16V6H4v10l2 2h8a1 1 0 001-1zM3 11h10M16 16l4-4m0 0l-4-4m4 4H10"></path>
          </svg>

          <svg *ngIf="pedido.status === 'EM_PREPARO' || pedido.status === 'RECEBIDO'"
               class="w-6 h-6 animate-spin" fill="none" stroke="currentColor" viewBox="0 0 24 24">
             <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9H3m4.582-5A8.003 8.003 0 0121 9h-1.582m-1.43 10.01A8.003 8.003 0 013 15h1.582m14.8-2a8.001 8.001 0 00-15.356-2H21"></path>
          </svg>

          <span>{{ formatarStatus(pedido.status) }}</span>
        </div>

        <!-- DETALHES -->
        <div class="mt-3 text-sm text-gray-600 space-y-1">
          <p><strong class="text-gray-700">Endereço:</strong> {{ pedido.enderecoCliente }}</p>
          <p><strong class="text-gray-700">Pagamento:</strong> {{ pedido.formaPagamento || 'Dinheiro' }}</p>
        </div>

        <!-- BOTÕES -->
        <div class="mt-4 flex gap-3 justify-end">
          <button *ngIf="pedido.status === 'EM_PREPARO'"
                  (click)="marcarComoPronto(pedido)"
                  class="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded-lg transition">
            Marcar como Pronto
          </button>

          <button *ngIf="pedido.status === 'PRONTO'"
                  (click)="enviarParaEntrega(pedido)"
                  class="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg transition">
            Enviar para Entrega
          </button>
        </div>
      </div>
    </div>
  </div>
</main>
