import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormBuilder, FormGroup, Validators, ReactiveFormsModule } from '@angular/forms';
import { PedidoService, PedidoCompleto } from '../../services/pedido.service';

@Component({
  selector: 'app-order-tracker',
  standalone: true,
  imports: [
    CommonModule,
    ReactiveFormsModule // Importa o ReactiveFormsModule
  ],
  templateUrl: './order-tracker.component.html',
  styleUrls: ['./order-tracker.component.css']
})
export class OrderTrackerComponent {

  searchForm: FormGroup;
  pedidoEncontrado: PedidoCompleto | null = null;
  isLoading = false;
  error: string | null = null;

  constructor(
    private fb: FormBuilder,
    private pedidoService: PedidoService
  ) {
    this.searchForm = this.fb.group({
      pedidoId: ['', [Validators.required, Validators.pattern("^[0-9]*$")]]
    });
  }

  // Chamado quando o cliente clica em "Buscar Pedido"
  onSearch(): void {
    if (this.searchForm.invalid) {
      this.error = "Por favor, insira um ID de pedido v√°lido.";
      return;
    }

    this.isLoading = true;
    this.error = null;
    this.pedidoEncontrado = null;

    const id = this.searchForm.value.pedidoId;

    // Chama o PedidoService (que j√° temos!)
    this.pedidoService.getPedidoById(id).subscribe({
      next: (pedido) => {
        this.pedidoEncontrado = pedido;
        this.isLoading = false;
      },
      error: (err) => {
        console.error('Erro ao buscar pedido:', err);
        this.error = `Pedido #${id} n√£o encontrado. Verifique o n√∫mero e tente novamente.`;
        this.isLoading = false;
      }
    });
  }
}
<!-- order-tracker.component.html -->
<main class="min-h-screen bg-gray-50">
  <div class="max-w-6xl mx-auto px-4 md:px-6 lg:px-8 py-8">

    <!-- Header -->
    <section class="text-center mb-8">
      <h1 class="text-3xl md:text-4xl font-extrabold tracking-tight text-gray-900">
        Meus Pedidos
      </h1>
      <p class="mt-2 text-gray-500">
        Acompanhe o status dos seus pedidos
      </p>
    </section>

    <section class="bg-white rounded-xl shadow-sm ring-1 ring-black/5 overflow-hidden">
      <header class="bg-orange-500 text-white px-4 py-3 font-semibold">
        Meus Pedidos
      </header>

      <!-- Se tiver formul√°rio de busca por ID, coloca aqui -->
      <div class="p-4 border-b border-gray-200">
        <form [formGroup]="searchForm" (ngSubmit)="onSearch()" class="flex gap-3">
          <input type="text" formControlName="pedidoId" placeholder="ID do Pedido (Ex: 2)"
                 class="flex-grow w-full rounded-md ring-1 ring-gray-300 px-3 py-2 outline-none focus:ring-2 focus:ring-orange-500">
          <button type="submit" [disabled]="isLoading || searchForm.invalid"
                  class="rounded-md px-4 py-2 text-white font-semibold transition
                         disabled:opacity-60"
                  [ngClass]="{'bg-orange-500 hover:bg-orange-600': !isLoading, 'bg-gray-400': isLoading}">
            {{ isLoading ? 'Buscando...' : 'Buscar' }}
          </button>
        </form>

        <div *ngIf="error" class="mt-3 bg-red-50 border border-red-200 text-red-700 rounded-md px-3 py-2">
          {{ error }}
        </div>
      </div>

      <div class="divide-y divide-gray-200">
        <!-- Card de pedido -->
        <article *ngFor="let p of orders; trackBy: trackByOrder" class="p-4">
          <div class="flex items-start justify-between text-sm text-gray-600">
            <div class="font-semibold text-gray-800">Pedido #{{ p.code }}</div>
            <div class="font-semibold text-emerald-600">{{ p.total | currency:'BRL' }}</div>
          </div>
          <div class="text-xs text-gray-500 mt-1">
            {{ p.date | date:'dd/MM/yyyy \'√†s\' HH:mm' }}
          </div>

          <!-- faixa de status -->
          <div class="mt-4 rounded-lg"
               [ngClass]="{
                 'bg-green-400 text-gray-900': p.status === 'pronto',
                 'bg-blue-300 text-gray-900': p.status === 'a_caminho',
                 'bg-yellow-300 text-gray-900': p.status === 'preparando'
               }">
            <div class="px-4 py-3 flex items-center gap-2">
              <ng-container [ngSwitch]="p.status">
                <span *ngSwitchCase="'pronto'">‚úÖ</span>
                <span *ngSwitchCase="'a_caminho'">üöö</span>
                <span *ngSwitchDefault>‚è≥</span>
              </ng-container>
              <span class="font-semibold capitalize">
                {{ p.status | titlecase }}
              </span>
            </div>
          </div>

          <div class="mt-4 space-y-1 text-sm text-gray-700">
            <div><span class="font-medium">Itens:</span> {{ p.items }}</div>
            <div><span class="font-medium">Endere√ßo:</span> {{ p.address }}</div>
            <div><span class="font-medium">Pagamento:</span> {{ p.payment | uppercase }}</div>
          </div>
        </article>

        <!-- vazio -->
        <article *ngIf="!orders || orders.length === 0" class="p-6 text-center text-gray-500">
          Nenhum pedido encontrado.
        </article>
      </div>
    </section>

  </div>
</main>
