import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable, BehaviorSubject } from 'rxjs'; // 1. Importar BehaviorSubject
import { tap } from 'rxjs/operators'; // 2. Importar 'tap'

// --- Interfaces (inalteradas) ---
export interface ItemPedido {
  dish: { id: number };
  quantidade: number;
}
// ... (resto das interfaces Pedido, Dish, ItemPedidoCompleto) ...
export interface PedidoCompleto {
  id: number;
  nomeCliente: string;
  telefoneCliente: string;
  enderecoCliente: string;
  dataCriacao: string; 
  status: string; 
  total: number;
  itens: ItemPedidoCompleto; 
  isLoading?: boolean;
}

@Injectable({
  providedIn: 'root'
})
export class PedidoService {

  private readonly apiUrl = 'http://localhost:8080/api/pedidos';

  // --- NOSSA FONTE ÚNICA DA VERDADE ---
  // 3. Um "sinalizador" que guarda a lista de pedidos
  private pedidosState = new BehaviorSubject<PedidoCompleto[]>([]);
  
  // 4. Um Observable público que os componentes podem "ouvir"
  public pedidos$ = this.pedidosState.asObservable();

  constructor(private http: HttpClient) { }

  /**
   * 5. Nova função: Busca todos os pedidos da API e "avisa"
   * a todos os ouvintes (o BehaviorSubject)
   */
  public loadAllPedidos(): void {
    this.http.get<PedidoCompleto[]>(this.apiUrl).subscribe({
      next: (data) => this.pedidosState.next(data), // Envia a nova lista para todos
      error: (err) => console.error('Erro ao carregar pedidos:', err)
      // Em um app real, trataríamos esse erro
    });
  }

  // --- MÉTODOS QUE MUDAM O ESTADO ---
  // (Agora eles também disparam a atualização)

  /**
   * Cria um novo pedido E ATUALIZA A LISTA
   */
  public createPedido(pedidoData: Pedido): Observable<PedidoCompleto> {
    return this.http.post<PedidoCompleto>(this.apiUrl, pedidoData).pipe(
      tap(() => {
        this.loadAllPedidos(); // 6. Após criar, recarrega a lista
      })
    );
  }

  /**
   * Busca um pedido (não mexe na lista principal, então fica igual)
   */
  public getPedidoById(id: number): Observable<PedidoCompleto> {
    return this.http.get<PedidoCompleto>(`${this.apiUrl}/${id}`);
  }

  /**
   * Busca todos os pedidos (NÃO USAR MAIS DIRETAMENTE, usar pedidos$)
   * Deixamos aqui por segurança, mas loadAllPedidos é o novo padrão.
   */
  public getAllPedidos(): Observable<PedidoCompleto[]> {
    return this.http.get<PedidoCompleto[]>(this.apiUrl);
  }

  /**
   * Atualiza o status E ATUALIZA A LISTA
   */
  updatePedidoStatus(id: number, novoStatus: string): Observable<PedidoCompleto> {
    const body = { status: novoStatus }; 
    return this.http.patch<PedidoCompleto>(`${this.apiUrl}/${id}/status`, body).pipe(
      tap(() => {
        this.loadAllPedidos(); // 7. A MÁGICA: Após o patch, recarrega a lista
      })
    );
  }
}
