import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormBuilder, FormGroup, Validators, ReactiveFormsModule } from '@angular/forms';
import { Router } from '@angular/router';
import { Observable } from 'rxjs';

// Nossos serviços
import { CartService, CartItem } from '../../services/cart.service';
import { PedidoService, ItemPedido, Pedido } from '../../services/pedido.service';

@Component({
  selector: 'app-checkout',
  standalone: true,
  imports: [
    CommonModule,
    ReactiveFormsModule // Precisamos de formulários reativos aqui
  ],
  templateUrl: './checkout.component.html',
  styleUrls: ['./checkout.component.css']
})
export class CheckoutComponent implements OnInit {

  customerForm: FormGroup;
  items$: Observable<CartItem[]>; // "Ouvinte" para os itens do carrinho
  total$: Observable<number>;     // "Ouvinte" para o total do carrinho

  isLoading = false;
  error: string | null = null;
  successMessage: string | null = null;

  constructor(
    private fb: FormBuilder,
    private cartService: CartService,
    private pedidoService: PedidoService,
    private router: Router
  ) {
    // Inicializa o formulário do cliente
    this.customerForm = this.fb.group({
      nomeCliente: ['', Validators.required],
      telefoneCliente: ['', Validators.required],
      enderecoCliente: ['', Validators.required]
    });

    // Conecta os Observables do serviço ao nosso componente
    this.items$ = this.cartService.items$;
    this.total$ = this.cartService.total$;
  }

  ngOnInit(): void {
    // Se o carrinho estiver vazio, redireciona para o cardápio
    if (this.cartService.getCurrentItems().length === 0) {
      this.router.navigate(['/']); // (Vamos mudar para '/menu' depois)
    }
  }

  // Chamado quando o cliente clica em "Finalizar Pedido"
  onSubmit(): void {
    if (this.customerForm.invalid) {
      this.customerForm.markAllAsTouched();
      return;
    }

    if (this.cartService.getCurrentItems().length === 0) {
      this.error = "Seu carrinho está vazio!";
      return;
    }

    this.isLoading = true;
    this.error = null;
    this.successMessage = null;

    // 1. Formata os itens do carrinho para o formato que o Backend (Java) espera
    const itensParaApi: ItemPedido[] = this.cartService.getCurrentItems().map(item => ({
      dish: { id: item.dish.id! }, // Envia apenas o ID do prato
      quantidade: item.quantidade
    }));

    // 2. Monta o objeto Pedido completo
    const novoPedido: Pedido = {
      ...this.customerForm.value, // Pega (nome, telefone, endereco) do formulário
      itens: itensParaApi
    };

    console.log('Enviando pedido para a API:', novoPedido);

    // 3. Chama o PedidoService para enviar ao backend
    this.pedidoService.createPedido(novoPedido).subscribe({
      next: (pedidoSalvo) => {
        console.log('Pedido criado com sucesso!', pedidoSalvo);
        this.successMessage = `Pedido #${pedidoSalvo.id} realizado com sucesso! Em breve sairá para entrega.`;
        this.isLoading = false;
        this.cartService.clearCart(); // Limpa o carrinho
        this.customerForm.reset();

        // (No futuro, podemos navegar para uma tela de "Obrigado" ou "Rastreamento")
        // setTimeout(() => this.router.navigate(['/']), 5000);
      },
      error: (err) => {
        console.error('Erro ao criar pedido:', err);
        this.error = 'Não foi possível finalizar o pedido. Tente novamente.';
        this.isLoading = false;
      }
    });
  }

  // --- Funções Auxiliares para o HTML ---
  isFieldInvalid(fieldName: string): boolean {
    const control = this.customerForm.get(fieldName);
    return control ? control.invalid && (control.dirty || control.touched) : false;
  }
}
<header class="bg-white border-b border-gray-200 sticky top-0 z-50 w-full">
  <div class="max-w-6xl mx-auto px-4 py-3 flex items-center gap-3">
    <span class="px-3 py-1 rounded-lg bg-[#ff6f5e] text-white text-sm">Bem-vindo!</span>

    <nav class="ml-auto flex gap-2">
      <a routerLink="/cardapio"
         class="px-4 py-2 rounded-md border border-gray-300 bg-white text-gray-800 hover:bg-gray-50 transition">
        Cardápio
      </a>
      <a routerLink="/checkout"
         class="px-4 py-2 rounded-md border border-gray-300 bg-white text-gray-800 hover:bg-gray-50 transition">
        Carrinho
      </a>
    </nav>
  </div>
</header>

<section class="max-w-6xl mx-auto px-4 pt-6 pb-6 text-center">
  <h1 class="text-3xl font-extrabold text-gray-900">Carrinho</h1>
  <p class="text-gray-500 mt-2">Revise seus itens e finalize o pedido</p>
</section>

<div class="max-w-6xl mx-auto px-4 pb-24 grid grid-cols-1 md:grid-cols-2 gap-8">

  <!-- LISTA DO CARRINHO -->
  <div class="rounded-2xl border border-gray-200 bg-white shadow-sm">
    <div class="bg-[#ff6f5e] text-white px-4 py-3 font-semibold rounded-t-2xl">Carrinho</div>

    <!-- Cabeçalho “tabela” -->
    <div class="grid grid-cols-12 bg-[#ff6f5e] text-white text-sm font-medium border-y border-white/30">
      <div class="col-span-6 px-4 py-2">Nome</div>
      <div class="col-span-3 px-4 py-2">Preço</div>
      <div class="col-span-3 px-4 py-2">Ação</div>
    </div>

    <!-- Itens -->
    <ng-container *ngIf="(items$ | async) as items">
      <div *ngFor="let item of items; trackBy: trackByItem"
           class="grid grid-cols-12 items-center bg-[#ffd316] border-b border-white/40 last:border-b-0 relative">

        <!-- Nome -->
        <div class="col-span-6 px-4 py-3 text-gray-900 font-medium truncate">
          {{ item.quantidade }}x {{ item.dish.name }}
        </div>

        <!-- Preço -->
        <div class="col-span-3 px-4 py-3 text-gray-900 font-semibold">
          {{ (item.dish.price * item.quantidade) | currency:'BRL' }}
        </div>

        <!-- Ações -->
        <div class="col-span-3 px-4 py-3 min-w-0">
          <div class="flex items-center gap-2 flex-wrap md:flex-nowrap whitespace-normal md:whitespace-nowrap">
            <button type="button"
                    (click)="decrement(item)"
                    class="w-7 h-7 rounded-full border border-gray-300 bg-white text-gray-700 grid place-items-center select-none">−</button>

            <span class="inline-flex items-center justify-center min-w-[2.25rem] h-7 rounded-full bg-white border border-gray-300 text-gray-800 text-sm select-none">
              {{ item.quantidade }}
            </span>

            <button type="button"
                    (click)="increment(item)"
                    class="w-7 h-7 rounded-full border border-gray-300 bg-white text-gray-700 grid place-items-center select-none">+</button>

            <button type="button"
                    (click)="remove(item)"
                    class="ml-2 px-3 py-1.5 rounded-md border border-gray-300 bg-white text-gray-700 text-sm">
              Remover
            </button>
          </div>
        </div>
      </div>

      <!-- Total -->
      <div class="px-4 py-3 flex justify-end" *ngIf="items.length > 0">
        <div class="text-right">
          <div class="text-sm text-gray-500">Total:</div>
          <div class="text-xl font-extrabold text-gray-900">
            {{ (total$ | async) | currency:'BRL' }}
          </div>
        </div>
      </div>

      <!-- Carrinho vazio -->
      <div *ngIf="items.length === 0"
           class="px-4 py-10 text-center text-gray-500">
        Seu carrinho está vazio.
      </div>
    </ng-container>

    <!-- Erro -->
    <div *ngIf="error"
         class="m-4 bg-red-50 text-red-700 border border-red-200 rounded-lg px-4 py-3">
      {{ error }}
    </div>
  </div>

  <!-- FORM DE PEDIDO -->
  <div class="rounded-2xl border border-gray-200 bg-white shadow-sm">
    <div class="bg-[#ff6f5e] text-white px-4 py-3 font-semibold rounded-t-2xl">Fazer Pedido</div>

    <div class="p-5">
      <form [formGroup]="customerForm" (ngSubmit)="onSubmit()" class="space-y-5">
        <div>
          <label class="block text-sm font-medium text-gray-700 mb-1">Cliente</label>
          <input type="text"
                 formControlName="nomeCliente"
                 placeholder="Nome do cliente"
                 class="w-full rounded-lg border border-gray-300 px-4 py-2.5 focus:outline-none focus:ring-2 focus:ring-[#ff6f5e] focus:border-transparent" />
          <p *ngIf="isFieldInvalid('nomeCliente')" class="text-xs text-red-600 mt-1">Informe o nome.</p>
        </div>

        <div>
          <label class="block text-sm font-medium text-gray-700 mb-1">Telefone</label>
          <input type="tel"
                 formControlName="telefoneCliente"
                 placeholder="(11) 99999-9999"
                 class="w-full rounded-lg border border-gray-300 px-4 py-2.5 focus:outline-none focus:ring-2 focus:ring-[#ff6f5e] focus:border-transparent" />
          <p *ngIf="isFieldInvalid('telefoneCliente')" class="text-xs text-red-600 mt-1">Informe o telefone.</p>
        </div>

        <div>
          <label class="block text-sm font-medium text-gray-700 mb-1">Endereço</label>
          <input type="text"
                 formControlName="enderecoCliente"
                 placeholder="Rua, número, bairro, cidade..."
                 class="w-full rounded-lg border border-gray-300 px-4 py-2.5 focus:outline-none focus:ring-2 focus:ring-[#ff6f5e] focus:border-transparent" />
          <p *ngIf="isFieldInvalid('enderecoCliente')" class="text-xs text-red-600 mt-1">Informe o endereço.</p>
        </div>

        <div class="pt-2">
          <button type="submit"
                  [disabled]="isLoading || (items$ | async)?.length === 0 || customerForm.invalid"
                  class="px-5 w-full py-3 rounded-lg text-white font-semibold transition
                         disabled:bg-gray-400 disabled:cursor-not-allowed bg-[#ff6f5e] hover:opacity-90">
            {{ isLoading ? 'Enviando Pedido...' : 'Fazer Pedido' }}
          </button>
        </div>

        <div *ngIf="successMessage"
             class="bg-green-50 text-green-700 border border-green-200 rounded-lg px-4 py-3">
          {{ successMessage }}
        </div>
      </form>
    </div>
  </div>
</div>
