import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';

// Importa a Topbar do Admin
import { TopbarComponent } from '../topbarAdmin/topbarAdmin.component';

// Importa o servi√ßo e as interfaces
import { PedidoService, PedidoCompleto } from '../../../services/pedido.service';

@Component({
  selector: 'app-delivery-order',
  standalone: true,
  imports: [
    CommonModule,
    TopbarComponent
  ],
  templateUrl: './delivery-order.component.html',
  styleUrls: ['./delivery-order.component.css']
})
export class DeliveryOrderComponent implements OnInit {

  public pedidosDeEntrega: PedidoCompleto[] = [];
  public isLoading = false;
  public error: string | null = null;
  public isUpdating: number | null = null; // Rastreia o ID do pedido sendo atualizado

  constructor(private pedidoService: PedidoService) { }

  ngOnInit(): void {
    // Primeiro carrega os pedidos, depois se inscreve para receber atualiza√ß√µes
    this.pedidoService.loadAllPedidos(); // Dispara o carregamento inicial
    this.subscribeToPedidos();
  }

  /**
   * Se inscreve no Observable de pedidos do servi√ßo para receber atualiza√ß√µes em tempo real.
   */
  subscribeToPedidos(): void {
    this.isLoading = true;
    this.error = null;

    this.pedidoService.pedidos$.subscribe({
      next: (data) => {
        // Filtra para exibir apenas os pedidos relevantes para a entrega
        // Os pedidos devem permanecer vis√≠veis enquanto tiverem esses status
        // Quando um pedido est√° PRONTO e muda para SAIU_PARA_ENTREGA, ele aparece aqui

        const statusRelevantes = ['PRONTO', 'SAIU_PARA_ENTREGA', 'ENTREGUE']; // <-- MUDAN√áA AQUI
        this.pedidosDeEntrega = data
          .filter(pedido => pedido && pedido.status && statusRelevantes.includes(pedido.status))
          .sort((a, b) => (a.id! < b.id! ? -1 : 1)); // Ordena por ID crescente

        this.isLoading = false;
      },
      error: (err) => {
        console.error('Erro ao buscar pedidos de entrega:', err);
        this.error = 'N√£o foi poss√≠vel carregar os pedidos.';
        this.isLoading = false;
      }
    });
  }

  // <-- FUN√á√ÉO ADICIONADA AQUI
  /**
   * Chamado quando o bot√£o "Enviar para Entrega" √© clicado.
   */
  enviarParaEntrega(pedido: PedidoCompleto): void {
    if (!pedido.id) return;
    this.isUpdating = pedido.id;

    this.pedidoService.updatePedidoStatus(pedido.id, 'SAIU_PARA_ENTREGA').subscribe({
       next: () => {
         this.isUpdating = null; // Sucesso
       },
       error: (err) => {
         this.handleUpdateError(err, pedido);
       }
    });
  }

  /**
   * Chamado quando o bot√£o "Marcar como Entregue" √© clicado.
   */
  marcarComoEntregue(pedido: PedidoCompleto): void {
    if (!pedido.id) return;
    this.isUpdating = pedido.id;

    this.pedidoService.updatePedidoStatus(pedido.id, 'ENTREGUE').subscribe({
      next: () => {
        this.isUpdating = null; // Sucesso, para de carregar
      },
      error: (err) => {
        this.handleUpdateError(err, pedido);
      }
    });
  }

  /**
   * Centraliza o tratamento de erros para atualiza√ß√µes de status.
   */
  private handleUpdateError(err: any, pedido: PedidoCompleto): void {
    console.error(`Erro ao atualizar status do pedido #${pedido.id}:`, err);
    alert(`N√£o foi poss√≠vel atualizar o Pedido #${pedido.id}. Tente novamente.`);
    this.isUpdating = null; // Garante que o loading pare em caso de erro.
  }
}
<app-topbar></app-topbar>

<main class="max-w-6xl mx-auto px-4 pt-8 pb-24">
  <section class="text-center mb-8">
    <h1 class="text-3xl text-gray-900">Gest√£o de Entregas</h1>
    <p class="text-gray-500 mt-2">Pedidos em rota e finalizados</p>
  </section>

  <div *ngIf="isLoading" class="p-8 text-center text-gray-500">
    Buscando pedidos...
  </div>
  <div *ngIf="error" class="p-8 m-4 bg-red-50 text-red-700 border border-red-200 rounded-lg text-center">
    {{ error }}
  </div>
  <div *ngIf="!isLoading && !error && pedidosDeEntrega.length === 0" class="p-8 text-center text-gray-500 border-2 border-dashed rounded-lg">
    Nenhum pedido em rota de entrega.
  </div>

  <div class="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">

    <div *ngFor="let pedido of pedidosDeEntrega"
         class="bg-white rounded-lg shadow-md border border-gray-200 flex flex-col">

      <div class="p-4 border-b bg-gray-50 rounded-t-lg">
        <h2 class="text-xl font-bold text-gray-800">Pedido #{{ pedido.id }}</h2>
        <p class="text-sm text-gray-600">Cliente: {{ pedido.nomeCliente }}</p>
        <p class="text-sm text-gray-500 mt-1">Endere√ßo: {{ pedido.enderecoCliente }}</p>
      </div>

      <div class="p-4 space-y-2 flex-grow">
        <h3 class="font-semibold text-gray-700">Itens:</h3>
        <ul class="list-disc list-inside text-gray-800">
          <li *ngFor="let item of pedido.itens">
            {{ item.quantidade }}x {{ item.dish.name }}
          </li>
        </ul>
      </div>

      <div class="p-4 bg-gray-50 border-t rounded-b-lg">

        <button *ngIf="pedido.status === 'PRONTO'"
                (click)="enviarParaEntrega(pedido)"
                [disabled]="isUpdating === pedido.id"
                class="w-full px-4 py-3 rounded-lg text-white font-semibold transition
                       bg-blue-500 hover:bg-blue-600
                       disabled:bg-gray-400 disabled:cursor-wait">
          {{ isUpdating === pedido.id ? 'Enviando...' : 'üöö Enviar para Entrega' }}
        </button>

        <button *ngIf="pedido.status === 'SAIU_PARA_ENTREGA'"
                (click)="marcarComoEntregue(pedido)"
                [disabled]="isUpdating === pedido.id"
                class="w-full px-4 py-3 rounded-lg text-white font-semibold transition
                       bg-green-500 hover:bg-green-600
                       disabled:bg-gray-400 disabled:cursor-wait">
          {{ isUpdating === pedido.id ? 'Finalizando...' : '‚úÖ Marcar como Entregue' }}
        </button>

        <div *ngIf="pedido.status === 'ENTREGUE'" class="text-center font-semibold text-green-700 bg-green-100 p-3 rounded-lg">
          Pedido Entregue
        </div>
      </div>
    </div>

  </div>
</main>
