import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterLink } from '@angular/router';
import { Observable } from 'rxjs'; // Importa o Observable
import { map } from 'rxjs/operators'; // <-- 1. IMPORTAR O OPERATOR 'map'

// NOSSOS SERVIÇOS
import { Dish, DishService } from '../../services/dish.service';
import { CartService, CartItem } from '../../services/cart.service'; // <-- 1. IMPORTA O CART SERVICE

@Component({
  selector: 'app-dish-list',
  standalone: true,
  imports: [
    CommonModule,
    RouterLink
  ],
  templateUrl: './dish-list.component.html',
  styleUrls: ['./dish-list.component.css']
})
export class DishListComponent implements OnInit {

  dishes: Dish[] = [];
  isLoading = true;
  error: string | null = null;

  // Observables para o resumo do carrinho
  public totalItensNoCarrinho$: Observable<number>;
  public totalPrecoCarrinho$: Observable<number>;

  // 2. INJETA O CART SERVICE (além do DishService)
  constructor(
    private dishService: DishService,
    private cartService: CartService // <-- INJETADO AQUI
  ) {
    // 3. Conecta os Observables do carrinho para o HTML
    this.totalPrecoCarrinho$ = this.cartService.total$;
    this.totalItensNoCarrinho$ = this.cartService.items$.pipe(
      // Calcula o número total de itens (soma das quantidades)
      // 2. ADICIONA OS TIPOS (CartItem[], total, item) para o modo 'strict'
      map((items: CartItem[]) => items.reduce((total: number, item: CartItem) => total + item.quantidade, 0))
    );
  }

  ngOnInit(): void {
    this.loadDishes();
  }

  loadDishes(): void {
    this.isLoading = true;
    this.error = null;
    this.dishService.getDishes().subscribe({
      next: (data) => {
        this.dishes = data;
        this.isLoading = false;
        console.log('Pratos carregados:', data);
      },
      error: (err) => {
        console.error('Erro ao buscar pratos:', err);
        this.error = 'Não foi possível carregar o cardápio. O backend está rodando?';
        this.isLoading = false;
      }
    });
  }

  // 4. NOVO MÉTODO: Chamado quando o cliente clica em "Adicionar"
  adicionarAoCarrinho(dish: Dish): void {
    console.log('Adicionando ao carrinho:', dish.name);
    this.cartService.addItem(dish);
  }

  // (O método deleteDish pode ficar, é útil para o admin)
  deleteDish(id: number | undefined): void {
    if (!id) return;
    if (confirm('Tem certeza que deseja deletar este prato?')) {
      this.dishService.deleteDish(id).subscribe({
        next: () => {
          console.log('Prato deletado com sucesso');
          this.dishes = this.dishes.filter(dish => dish.id !== id);
        },
        error: (err) => {
          this.error = 'Erro ao deletar o prato.';
        }
      });
    }
  }
}
<!-- Cabeçalho (O mesmo de antes, com todos os links) -->
<header class="bg-white shadow-md p-4">
  <div class="container mx-auto flex justify-between items-center">
    <h1 class="text-2xl font-bold text-blue-600">iBeus Delivery</h1>
    <nav class="flex gap-4">
      <a [routerLink]="['/tracker']" class="text-gray-600 hover:text-blue-600 transition duration-300">Acompanhar Pedido</a>
      <a [routerLink]="['/add-dish']" class="text-gray-600 hover:text-blue-600 transition duration-300">Adicionar Prato (Admin)</a>
      <a [routerLink]="['/admin']" class="text-gray-600 hover:text-blue-600 transition duration-300">Painel da Cozinha</a>
    </nav>
  </div>
</header>

<!-- Conteúdo Principal -->
<div class="container mx-auto p-6">

  <!-- Feedback de Carregamento -->
  <div *ngIf="isLoading" class="text-center py-20">
    <p class="text-2xl text-gray-600">Carregando cardápio...</p>
    <!-- (Opcional: Adicionar um spinner CSS aqui) -->
  </div>

  <!-- Feedback de Erro -->
  <div *ngIf="error" class="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded-lg max-w-2xl mx-auto" role="alert">
    <strong class="font-bold">Erro!</strong>
    <span class="block sm:inline">{{ error }}</span>
  </div>

  <!-- Cardápio Vazio -->
  <div *ngIf="!isLoading && !error && dishes.length === 0" class="text-center py-20 bg-white rounded-xl shadow-lg">
    <h2 class="text-3xl font-semibold text-gray-700 mb-4">Cardápio Vazio</h2>
    <p class="text-gray-500 mb-6">Ainda não há pratos cadastrados.</p>
    <a [routerLink]="['/add-dish']" class="bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-lg shadow-md transition duration-300">
      Adicionar o primeiro prato
    </a>
  </div>

  <!-- ########## A NOVA GRELHA (GRID) DE PRATOS ########## -->
  <div *ngIf="!isLoading && !error && dishes.length > 0" class="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">

    <!-- Loop *ngFor para cada 'dish' (prato) -->
    <div *ngFor="let dish of dishes" class="bg-white rounded-xl shadow-lg overflow-hidden transition-transform duration-300 hover:shadow-2xl hover:-translate-y-1 flex flex-col">

      <!-- Imagem do Prato -->
      <!-- Usa a imagem do 'dish.imageUrl' OU um placeholder se estiver vazia -->
      <img class="h-56 w-full object-cover"
           [src]="dish.imageUrl ? dish.imageUrl : 'https://placehold.co/600x400/E2E8F0/94A3B8?text=Sem+Imagem'"
           onerror="this.src='https://placehold.co/600x400/E2E8F0/94A3B8?text=Sem+Imagem'"
           alt="{{ dish.name }}">

      <!-- Conteúdo (Nome, Descrição, Preço) -->
      <div class="p-6 flex flex-col flex-grow">
        <h3 class="text-2xl font-bold text-gray-900 mb-2">{{ dish.name }}</h3>
        <p class="text-gray-600 mb-4 flex-grow">{{ dish.description }}</p>
        <span class="text-3xl font-bold text-green-600 mb-5">{{ dish.price | currency:'BRL' }}</span>

        <!-- Botões de Ação -->
        <div class="mt-auto">
          <button (click)="adicionarAoCarrinho(dish)" class="w-full bg-blue-600 hover:bg-blue-700 text-white font-bold py-3 px-4 rounded-lg shadow-md transition duration-300 mb-3">
            Adicionar ao Carrinho
          </button>

          <!-- Botões de Admin (Editar/Deletar) -->
          <div class="flex justify-between gap-3">
            <a [routerLink]="['/edit-dish', dish.id]" class="flex-1 text-center bg-gray-100 hover:bg-gray-200 text-gray-800 font-medium py-2 px-4 rounded-lg transition duration-300">
              Editar
            </a>
            <button (click)="deleteDish(dish.id)" class="flex-1 bg-red-100 hover:bg-red-200 text-red-700 font-medium py-2 px-4 rounded-lg transition duration-300">
              Deletar
            </button>
          </div>
        </div>
      </div>
    </div>

  </div>
</div>


<!-- ########## BARRA DE CHECKOUT (A mesma de antes) ########## -->
<ng-container *ngIf="(totalItensNoCarrinho$ | async) as totalItens">
  <div *ngIf="totalItens > 0"
       class="fixed bottom-0 left-0 right-0 bg-gradient-to-r from-blue-600 to-blue-800 text-white p-4 shadow-2xl-top animate-slide-up">
    <div class="container mx-auto flex justify-between items-center">
      <div>
        <span class="font-bold text-xl">{{ totalItens }} {{ totalItens === 1 ? 'item' : 'itens' }} no carrinho</span>
        <span class="ml-4 text-lg">{{ (totalPrecoCarrinho$ | async) | currency:'BRL' }}</span>
      </div>
      <a [routerLink]="['/checkout']" class="bg-white text-blue-700 font-bold py-2 px-6 rounded-lg shadow-md hover:bg-gray-100 transition duration-300">
        Finalizar Pedido
      </a>
    </div>
  </div>
</ng-container>

<!-- Para a animação da barra de checkout -->
<style>
  @keyframes slide-up {
    from { transform: translateY(100%); }
    to { transform: translateY(0); }
  }
  .animate-slide-up {
    animation: slide-up 0.3s ease-out forwards;
  }
  .shadow-2xl-top {
    box-shadow: 0 -10px 15px -3px rgba(0, 0, 0, 0.1), 0 -4px 6px -2px rgba(0, 0, 0, 0.05);
  }
</style>
