import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterLink } from '@angular/router';
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';

// NOSSOS SERVI√áOS
import { Dish, DishService } from '../../services/dish.service';
import { CartService, CartItem } from '../../services/cart.service';

@Component({
  selector: 'app-dish-list',
  standalone: true,
  imports: [
    CommonModule,
    RouterLink
  ],
  templateUrl: './dish-list.component.html',
  styleUrls: ['./dish-list.component.css']
})
export class DishListComponent implements OnInit {

  dishes: Dish[] = [];
  isLoading = true;
  error: string | null = null;

  public totalItensNoCarrinho$: Observable<number>;
  public totalPrecoCarrinho$: Observable<number>;

  constructor(
    private dishService: DishService,
    private cartService: CartService
  ) {
    this.totalPrecoCarrinho$ = this.cartService.total$;
    this.totalItensNoCarrinho$ = this.cartService.items$.pipe(
      map((items: CartItem[]) => items.reduce((total: number, item: CartItem) => total + item.quantidade, 0))
    );
  }

  ngOnInit(): void {
    this.loadDishes();
  }

  loadDishes(): void {
    this.isLoading = true;
    this.error = null;
    this.dishService.getDishes().subscribe({
      next: (data) => {
        this.dishes = data;
        this.isLoading = false;
        console.log('Pratos carregados:', data);
      },
      error: (err) => {
        console.error('Erro ao buscar pratos:', err);
        this.error = 'N√£o foi poss√≠vel carregar o card√°pio. O backend est√° rodando?';
        this.isLoading = false;
      }
    });
  }

  adicionarAoCarrinho(dish: Dish): void {
    console.log('Adicionando ao carrinho:', dish.name);
    this.cartService.addItem(dish);
  }

  // REMOVIDO: O m√©todo deleteDish() foi removido daqui.
}
<!-- ======= TOPBAR ======= -->
<header class="bg-[#ff6f5e]">
  <div class="max-w-6xl mx-auto px-4 py-3 flex items-center gap-3">
    
    <span class="inline-flex items-center gap-2 text-sm text-white font-medium">
      Bem-vindo! üßë‚Äçüç≥
    </span>

    <nav class="ml-auto flex gap-2">
      <a
        [routerLink]="['/cardapio']"
        class="px-4 py-2 rounded-md bg-white text-[#ff6f5e] font-medium hover:bg-gray-100 transition"
      >
        Card√°pio
      </a>

      <a
        [routerLink]="['/checkout']"
        class="px-4 py-2 rounded-md bg-white text-[#ff6f5e] font-medium hover:bg-gray-100 transition"
      >
        Carrinho ({{ (totalItensNoCarrinho$ | async) || 0 }})
      </a>

      <a
        [routerLink]="['/orders']"
        class="px-4 py-2 rounded-md bg-white text-[#ff6f5e] font-medium hover:bg-gray-100 transition"
      >
        Meus Pedidos
      </a>

      <a
        [routerLink]="['/login']"
        class="px-4 py-2 rounded-md bg-white text-[#ff6f5e] font-medium hover:bg-gray-100 transition"
      >
        Sair
      </a>
    </nav>
  </div>
</header>
<!-- ======= CONTE√öDO ======= -->
<main class="max-w-6xl mx-auto px-4 pt-8 pb-24">
  <!-- T√≠tulo central + subt√≠tulo -->
  <section class="text-center mb-8">
    <h1 class="text-3xl font-extrabold text-gray-900">Card√°pio</h1>
    <p class="text-gray-500 mt-2">Escolha seus pratos favoritos</p>
  </section>

  <!-- Estados -->
  <div *ngIf="isLoading" class="text-center py-20 text-gray-500">Carregando...</div>
  <div *ngIf="error" class="max-w-xl mx-auto mb-6 px-4 py-3 rounded-lg bg-red-50 text-red-700 border border-red-200">
    {{ error }}
  </div>
  <div *ngIf="!isLoading && !error && dishes.length === 0" class="text-center py-20">
    <h2 class="text-2xl font-semibold text-gray-800">Card√°pio vazio</h2>
    <p class="text-gray-500">Ainda n√£o h√° pratos cadastrados.</p>
  </div>

  <!-- GRID DE PRATOS -->
  <div *ngIf="!isLoading && !error && dishes.length > 0"
       class="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">

    <article *ngFor="let dish of dishes"
             class="rounded-xl border border-gray-200 bg-white shadow-sm overflow-hidden hover:shadow-md transition">
      <!-- Faixa amarela do card -->
      <div class="h-40 bg-[#ffd316] flex items-center justify-center">
        <!-- √çcone de talheres (SVG simples para lembrar o mock) -->
        <svg width="56" height="56" viewBox="0 0 24 24" fill="none" class="text-gray-800">
          <path d="M7 3v8M9 3v8M8 3v8M11 3v7a2 2 0 1 1-4 0V3M14 3v7a2 2 0 1 0 4 0V3"
                stroke="currentColor" stroke-width="1.5" stroke-linecap="round" />
          <circle cx="12" cy="14" r="3.5" stroke="currentColor" stroke-width="1.5"/>
        </svg>
      </div>

      <!-- Corpo do card -->
      <div class="p-5">
        <h3 class="text-lg font-semibold text-gray-900">{{ dish.name }}</h3>
        <p class="text-sm text-gray-600 mt-1 mb-3">{{ dish.description }}</p>

        <div class="text-[15px] font-semibold text-green-600 mb-4">
          {{ dish.price | currency:'BRL' }}
        </div>

        <button (click)="adicionarAoCarrinho(dish)"
                class="w-full rounded-md bg-[#ff6f5e] text-white font-medium py-2.5 hover:opacity-90 transition">
          Adicionar ao Carrinho
        </button>
      </div>
    </article>

  </div>
</main>

<!-- ======= BARRA FIXA DE CHECKOUT ======= -->
<ng-container *ngIf="(totalItensNoCarrinho$ | async) as totalItens">
  <div *ngIf="totalItens > 0"
       class="fixed bottom-0 inset-x-0 bg-white/95 backdrop-blur border-t border-gray-200">
    <div class="max-w-6xl mx-auto px-4 py-3 flex items-center justify-between">
      <div class="text-gray-800">
        <span class="font-semibold">{{ totalItens }} {{ totalItens === 1 ? 'item' : 'itens' }} no carrinho</span>
        <span class="ml-3 text-gray-600">
          {{ (totalPrecoCarrinho$ | async) | currency:'BRL' }}
        </span>
      </div>
      <a [routerLink]="['/checkout']"
         class="px-5 py-2.5 rounded-md bg-[#ff6f5e] text-white font-semibold hover:opacity-90 transition">
        Finalizar Pedido
      </a>
    </div>
  </div>
</ng-container>
