import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common'; // Necessário para *ngFor, *ngIf, | currency
import { RouterLink } from '@angular/router'; // Necessário para [routerLink]
import { Dish, DishService } from '../../services/dish.service'; // Importa o serviço e a interface

@Component({
  selector: 'app-dish-list',
  standalone: true, // A base do seu professor usa componentes standalone
  imports: [
    CommonModule, // Adiciona os módulos necessários
    RouterLink
  ],
  templateUrl: './dish-list.component.html',
  styleUrls: ['./dish-list.component.css']
})
export class DishListComponent implements OnInit {

  dishes: Dish[] = []; // Array para guardar os pratos vindos da API
  isLoading = true; // Variável para mostrar um feedback de "carregando"
  error: string | null = null; // Para guardar mensagens de erro

  // 1. Injeta o DishService (que o professor criou) no construtor
  constructor(private dishService: DishService) {}

  // 2. Este método roda assim que o componente é carregado
  ngOnInit(): void {
    this.loadDishes();
  }

  // 3. Método para buscar os pratos da API
  loadDishes(): void {
    this.isLoading = true;
    this.error = null;

    this.dishService.getDishes().subscribe({
      // 4. Se a chamada der Certo:
      next: (data) => {
        this.dishes = data; // Preenche o nosso array
        this.isLoading = false;
        console.log('Pratos carregados:', data);
      },
      // 5. Se a chamada der Errado:
      error: (err) => {
        console.error('Erro ao buscar pratos:', err);
        this.error = 'Não foi possível carregar o cardápio. O backend está rodando?';
        this.isLoading = false;
      }
    });
  }

  // 6. Método para deletar um prato
  deleteDish(id: number | undefined): void {
    if (!id) return; // Não faz nada se o ID for indefinido

    if (confirm('Tem certeza que deseja deletar este prato?')) {
      this.dishService.deleteDish(id).subscribe({
        next: () => {
          console.log('Prato deletado com sucesso');
          // Remove o prato da lista local para atualizar a tela
          this.dishes = this.dishes.filter(dish => dish.id !== id);
        },
        error: (err) => {
          console.error('Erro ao deletar prato:', err);
          this.error = 'Erro ao deletar o prato.';
        }
      });
    }
  }
}
<!-- Estilização inspirada no Figma (image_436bbd.png) mas focada no Cardápio (image_436b1d.png) -->
<div class="container mx-auto p-6 max-w-4xl">

  <div class="flex justify-between items-center mb-6">
    <h1 class="text-3xl font-bold text-gray-800">Cardápio</h1>
    <!-- Botão "Adicionar Prato" que leva para a rota de formulário -->
    <a [routerLink]="['/add-dish']" class="bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-lg shadow-md transition duration-300">
      Adicionar Prato
    </a>
  </div>

  <!-- Feedback de Erro -->
  <div *ngIf="error" class="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded-lg relative mb-6" role="alert">
    <strong class="font-bold">Erro: </strong>
    <span class="block sm:inline">{{ error }}</span>
  </div>

  <!-- Feedback de Carregamento -->
  <div *ngIf="isLoading" class="text-center py-10">
    <p class="text-gray-600 text-lg">Carregando cardápio...</p>
    <!-- Você pode adicionar um spinner de carregamento aqui -->
  </div>

  <!-- Lista de Pratos -->
  <div *ngIf="!isLoading && dishes.length > 0" class="grid grid-cols-1 md:grid-cols-2 gap-6">

    <!-- Loop *ngFor para iterar sobre cada 'dish' no array 'dishes' -->
    <div *ngFor="let dish of dishes" class="bg-white rounded-xl shadow-lg overflow-hidden transition-transform duration-300 hover:scale-105">
      <div class="p-6">
        <div class="flex justify-between items-start">
          <div>
            <h2 class="text-2xl font-semibold text-gray-900 mb-2">{{ dish.name }}</h2>
            <p class="text-gray-600 mb-4">{{ dish.description }}</p>
          </div>
          <!-- Preço formatado com o pipe 'currency' -->
          <span class="text-xl font-bold text-green-600 ml-4">{{ dish.price | currency:'BRL' }}</span>
        </div>

        <div class="flex justify-end gap-3 mt-4">
          <!-- Botão de Editar (leva para a rota de formulário com o ID) -->
          <a [routerLink]="['/edit-dish', dish.id]" class="text-sm font-medium text-blue-600 hover:text-blue-800 transition duration-300">
            Editar
          </a>
          <!-- Botão de Deletar (chama nosso método deleteDish()) -->
          <button (click)="deleteDish(dish.id)" class="text-sm font-medium text-red-600 hover:text-red-800 transition duration-300">
            Deletar
          </button>
        </div>
      </div>
    </div>
  </div>

  <!-- Mensagem de "Nenhum prato encontrado" -->
  <div *ngIf="!isLoading && dishes.length === 0 && !error" class="text-center py-10 bg-white rounded-lg shadow-md">
    <h2 class="text-2xl font-semibold text-gray-700 mb-2">Cardápio Vazio</h2>
    <p class="text-gray-500">Nenhum prato foi cadastrado ainda.</p>
  </div>

</div>
