import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';

// Importa a Topbar do Admin (como definido no seu TS)
import { TopbarComponent } from '../topbarAdmin/topbarAdmin.component';

// Importa o serviﾃｧo e as interfaces corretas
import { PedidoService, PedidoCompleto, ItemPedidoCompleto } from '../../../services/pedido.service'; 

@Component({
ﾂ selector: 'app-kitchen-orders', // seletor no plural
ﾂ standalone: true,
ﾂ imports: [
ﾂ ﾂ CommonModule,
ﾂ ﾂ TopbarComponent 
ﾂ ],
ﾂ templateUrl: './kitchen-orders.component.html',
ﾂ styleUrls: ['./kitchen-orders.component.css']
})
export class KitchenOrdersComponent implements OnInit { 

ﾂ public pedidosDaCozinha: PedidoCompleto[] = [];
ﾂ public isLoading = false;
ﾂ public error: string | null = null;

ﾂ constructor(private pedidoService: PedidoService) { }

ﾂ ngOnInit(): void {
ﾂ ﾂ this.carregarPedidos();
ﾂ }

ﾂ carregarPedidos(): void {
ﾂ ﾂ this.isLoading = true;
ﾂ ﾂ this.error = null;

ﾂ ﾂ this.pedidoService.getAllPedidos().subscribe({
ﾂ ﾂ ﾂ next: (data) => {
ﾂ ﾂ ﾂ ﾂ // --- AJUSTE PRINCIPAL ---
ﾂ ﾂ ﾂ ﾂ // Agora filtra tambﾃｩm por 'PENDENTE' (ou qualquer status inicial)
ﾂ ﾂ ﾂ ﾂ this.pedidosDaCozinha = data
ﾂ ﾂ ﾂ ﾂ ﾂ .filter(pedido => 
ﾂ ﾂ ﾂ ﾂ ﾂ ﾂ pedido.status === 'PENDENTE' ||  // <--- ADICIONADO
ﾂ ﾂ ﾂ ﾂ ﾂ ﾂ pedido.status === 'EM_PREPARO' || 
ﾂ ﾂ ﾂ ﾂ ﾂ ﾂ pedido.status === 'PRONTO'
ﾂ ﾂ ﾂ ﾂ ﾂ )
ﾂ ﾂ ﾂ ﾂ ﾂ .sort((a, b) => (a.id! < b.id! ? -1 : 1)); // Ordena por ID
ﾂ ﾂ ﾂ ﾂ 
ﾂ ﾂ ﾂ ﾂ this.isLoading = false;
ﾂ ﾂ ﾂ },
ﾂ ﾂ ﾂ error: (err) => {
ﾂ ﾂ ﾂ ﾂ console.error('Erro ao buscar pedidos da cozinha:', err);
ﾂ ﾂ ﾂ ﾂ this.error = 'Nﾃ｣o foi possﾃｭvel carregar os pedidos.';
ﾂ ﾂ ﾂ ﾂ this.isLoading = false;
ﾂ ﾂ ﾂ }
ﾂ ﾂ });
ﾂ }

ﾂ /**
ﾂ  * Chamado quando o botﾃ｣o "Aceitar Pedido" ﾃｩ clicado.
ﾂ  */
ﾂ aceitarPedido(pedido: PedidoCompleto): void {
ﾂ ﾂ if (!pedido.id) return;
ﾂ ﾂ pedido.isLoading = true; 

ﾂ ﾂ // Muda o status de PENDENTE para EM_PREPARO
ﾂ ﾂ this.pedidoService.updatePedidoStatus(pedido.id, 'EM_PREPARO').subscribe({
ﾂ ﾂ ﾂ next: (pedidoAtualizado) => {
ﾂ ﾂ ﾂ ﾂ pedido.status = 'EM_PREPARO'; // Atualiza o status localmente
ﾂ ﾂ ﾂ ﾂ pedido.isLoading = false;
ﾂ ﾂ ﾂ },
ﾂ ﾂ ﾂ error: (err) => {
ﾂ ﾂ ﾂ ﾂ console.error('Erro ao aceitar pedido:', err);
ﾂ ﾂ ﾂ ﾂ alert(`Nﾃ｣o foi possﾃｭvel aceitar o Pedido #${pedido.id}. Tente novamente.`);
ﾂ ﾂ ﾂ ﾂ pedido.isLoading = false; 
ﾂ ﾂ ﾂ }
ﾂ ﾂ });
ﾂ }

ﾂ /**
ﾂ ﾂ* Chamado quando o botﾃ｣o "Marcar como Pronto" ﾃｩ clicado.
ﾂ ﾂ*/
ﾂ marcarComoPronto(pedido: PedidoCompleto): void {
ﾂ ﾂ if (!pedido.id) return;
ﾂ ﾂ pedido.isLoading = true; 

ﾂ ﾂ this.pedidoService.updatePedidoStatus(pedido.id, 'PRONTO').subscribe({
ﾂ ﾂ ﾂ next: (pedidoAtualizado) => {
ﾂ ﾂ ﾂ ﾂ pedido.status = 'PRONTO';
ﾂ ﾂ ﾂ ﾂ pedido.isLoading = false;
ﾂ ﾂ ﾂ },
ﾂ ﾂ ﾂ error: (err) => {
ﾂ ﾂ ﾂ ﾂ console.error('Erro ao atualizar status:', err);
ﾂ ﾂ ﾂ ﾂ alert(`Nﾃ｣o foi possﾃｭvel atualizar o Pedido #${pedido.id}. Tente novamente.`);
ﾂ ﾂ ﾂ ﾂ pedido.isLoading = false; 
ﾂ ﾂ ﾂ }
ﾂ ﾂ });
ﾂ }

ﾂ /**
ﾂ ﾂ* (PRﾃ店IMO PASSO) Chamado quando o botﾃ｣o "Enviar para Entrega" ﾃｩ clicado.
ﾂ ﾂ*/
ﾂ enviarParaEntrega(pedido: PedidoCompleto): void {
ﾂ ﾂ if (!pedido.id) return;
ﾂ ﾂ pedido.isLoading = true; // Usa o mesmo 'isLoading'

ﾂ ﾂ this.pedidoService.updatePedidoStatus(pedido.id, 'SAIU_PARA_ENTREGA').subscribe({
ﾂ ﾂ ﾂ ﾂnext: (pedidoAtualizado) => {
ﾂ ﾂ ﾂ ﾂ ﾂ// Agora sim, removemos da lista da Cozinha/Prontos
ﾂ ﾂ ﾂ ﾂ ﾂthis.pedidosDaCozinha = this.pedidosDaCozinha.filter(p => p.id !== pedido.id);
ﾂ ﾂ ﾂ ﾂ},
ﾂ ﾂ ﾂ ﾂerror: (err) => {
ﾂ ﾂ ﾂ ﾂ ﾂalert(`Nﾃ｣o foi possﾃｭvel enviar o Pedido #${pedido.id} para entrega.`);
ﾂ ﾂ ﾂ ﾂ ﾂpedido.isLoading = false;
ﾂ ﾂ ﾂ ﾂ}
ﾂ ﾂ });
ﾂ }
}
