import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormBuilder, FormGroup, Validators, ReactiveFormsModule } from '@angular/forms';
import { Router } from '@angular/router';
import { Observable } from 'rxjs';

import { CartService, CartItem } from '../../services/cart.service';
// se voc√™ usa PedidoService, mantenha. Caso n√£o, pode remover os imports e o provider.
import { PedidoService, ItemPedido, Pedido } from '../../services/pedido.service';

@Component({
  selector: 'app-checkout',
  standalone: true,
  imports: [CommonModule, ReactiveFormsModule],
  templateUrl: './checkout.component.html',
})
export class CheckoutComponent implements OnInit {
  items$: Observable<CartItem[]> = this.cartService.items$;
  total$: Observable<number> = this.cartService.total$;

  customerForm: FormGroup;
  isLoading = false;
  successMessage = '';
  error = '';

  constructor(
    private fb: FormBuilder,
    private router: Router,
    public cartService: CartService,      // <<< usa cartService (n√£o alterei teu service)
    private pedidoService: PedidoService, // se n√£o usar, pode remover
  ) {
    this.customerForm = this.fb.group({
      nomeCliente: ['', [Validators.required, Validators.minLength(2)]],
      telefoneCliente: ['', [Validators.required]],
      enderecoCliente: ['', [Validators.required, Validators.minLength(5)]],
    });
  }

  ngOnInit(): void {
    // Se o carrinho estiver vazio, opcionalmente redireciona
    // if ((this.cartService.getCurrentItems() || []).length === 0) {
    //   this.router.navigate(['/cardapio']);
    // }
  }

  // === M√âTODOS CHAMADOS PELO TEMPLATE ===
  trackByItem(index: number, item: CartItem): number | string {
    return item?.dish?.id ?? index;
  }

  increment(item: CartItem): void {
    this.cartService.addItem(item.dish);
  }

  decrement(item: CartItem): void {
    if (!item) return;
    if (item.quantidade <= 1) {
      this.cartService.removeItem(item.dish.id);
      return;
    }
    // Sem alterar o CartService: remove e re-adiciona (qnt-1)
    this.cartService.removeItem(item.dish.id);
    for (let i = 0; i < item.quantidade - 1; i++) {
      this.cartService.addItem(item.dish);
    }
  }

  remove(item: CartItem): void {
    this.cartService.removeItem(item.dish.id);
  }

  // === SUBMIT DO PEDIDO ===
  onSubmit(): void {
    this.error = '';
    this.successMessage = '';

    if (this.customerForm.invalid) {
      this.customerForm.markAllAsTouched();
      return;
    }

    const items = this.cartService.getCurrentItems();
    if (!items.length) {
      this.error = 'Seu carrinho est√° vazio.';
      return;
    }

    // Se voc√™ tiver um PedidoService real, envie aqui:
    // (exemplo did√°tico; adapte ao seu backend)
    const payload: Pedido = {
      cliente: this.customerForm.value,
      itens: items.map<ItemPedido>(it => ({
        dishId: it.dish.id,
        nome: it.dish.name,
        precoUnitario: it.dish.price,
        quantidade: it.quantidade,
        subtotal: it.dish.price * it.quantidade
      })),
      total: items.reduce((t, it) => t + it.dish.price * it.quantidade, 0),
      criadoEm: new Date().toISOString(),
    };

    this.isLoading = true;
    this.pedidoService.criarPedido(payload).subscribe({
      next: () => {
        this.successMessage = 'Pedido enviado com sucesso! üëå';
        this.cartService.clearCart();
        this.customerForm.reset();
        this.isLoading = false;
        // this.router.navigate(['/sucesso']); // se quiser navegar
      },
      error: (err) => {
        console.error('Erro ao criar pedido:', err);
        this.error = 'N√£o foi poss√≠vel finalizar o pedido. Tente novamente.';
        this.isLoading = false;
      }
    });
  }

  // Aux√≠lio para mensagens de valida√ß√£o no template
  isFieldInvalid(fieldName: string): boolean {
    const control = this.customerForm.get(fieldName);
    return control ? control.invalid && (control.dirty || control.touched) : false;
  }
}
