  <div class="container mx-auto p-6 max-w-7xl">

  <!-- Cabeçalho do Admin -->
  <div class="flex justify-Qbetween items-center mb-8">
    <h1 class="text-3xl font-bold text-gray-800">Painel do Funcionário</h1>
    <a [routerLink]="['/']" class="bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-lg shadow-md transition duration-300">
      Ir para o Site
    </a>
  </div>


 <!-- ########## SEÇÃO 1: GERENCIAMENTO DE PRATOS ########## -->
 <div class="bg-white p-6">
  <div class="flex justify-between items-center mb-4">
    <h2 class="text-2xl font-semibold text-gray-800">Gerenciamento de Pratos (Cardápio)</h2>
  </div>

  <!-- Tabela de Pratos (Nova) -->
  <div *ngIf="isLoadingDishes" class="text-center py-5 text-gray-600">Carregando pratos...</div>
  <div *ngIf="errorDishes" class="bg-red-100 ...">{{ errorDishes }}</div>
  <div *ngIf="!isLoadingDishes && !errorDishes && dishes.length === 0" class="text-center py-10 ...">
