import { Component, OnInit, OnDestroy } from '@angular/core'; 
import { CommonModule } from '@angular/common';
import { TopbarComponent } from '../topbarAdmin/topbarAdmin.component';
import { PedidoService, PedidoCompleto } from '../../../services/pedido.service'; 
import { Subscription } from 'rxjs'; 

@Component({
  selector: 'app-kitchen-orders',
  standalone: true,
  imports: [ CommonModule, TopbarComponent ],
  templateUrl: './kitchen-orders.component.html',
  styleUrls: ['./kitchen-orders.component.css']
})
export class KitchenOrdersComponent implements OnInit, OnDestroy { 

  public pedidosDaCozinha: PedidoCompleto[] = [];
  public isLoading = false;
  public error: string | null = null;
  private pedidosSub!: Subscription; 

  constructor(private pedidoService: PedidoService) { }

  ngOnInit(): void {
    this.isLoading = true;
    this.pedidosSub = this.pedidoService.pedidos$.subscribe(data => {
      this.pedidosDaCozinha = data
        .filter(pedido => 
          pedido.status === 'RECEBIDO' || 
          pedido.status === 'EM_PREPARO' || 
          pedido.status === 'PRONTO'
        )
        .sort((a, b) => (a.id! < b.id! ? -1 : 1)); 
      
      this.isLoading = false;
    });

    this.pedidoService.loadAllPedidos();
  }

  ngOnDestroy(): void {
    if (this.pedidosSub) {
      this.pedidosSub.unsubscribe();
    }
  }

  aceitarPedido(pedido: PedidoCompleto): void {
    if (!pedido.id) return;
    pedido.isLoading = true; 

    this.pedidoService.updatePedidoStatus(pedido.id, 'EM_PREPARO').subscribe({
      next: () => pedido.isLoading = false,
      error: (err) => {
        console.error('Erro ao aceitar pedido:', err);
        alert(`N√£o foi poss√≠vel aceitar o Pedido #${pedido.id}.`);
        pedido.isLoading = false; 
      }
    });
  }

  marcarComoPronto(pedido: PedidoCompleto): void {
    if (!pedido.id) return;
    pedido.isLoading = true; 

    this.pedidoService.updatePedidoStatus(pedido.id, 'PRONTO').subscribe({
      next: () => {
        // Assim que ficar PRONTO, j√° vai para a entrega:
        setTimeout(() => {
          this.enviarParaEntrega(pedido);
        }, 800); // pequeno delay para UX melhor
      },
      error: (err) => {
        console.error('Erro ao atualizar status:', err);
        alert(`N√£o foi poss√≠vel atualizar o Pedido #${pedido.id}.`);
        pedido.isLoading = false; 
      }
    });
  }

  public enviarParaEntrega(pedido: PedidoCompleto): void {
    if (!pedido.id) return;
    this.pedidoService.updatePedidoStatus(pedido.id, 'SAIU_PARA_ENTREGA').subscribe({
      next: () => console.log(`Pedido #${pedido.id} enviado para entrega.`),
      error: (err) => {
        console.error('Erro ao enviar para entrega:', err);
        alert(`N√£o foi poss√≠vel enviar o Pedido #${pedido.id} para entrega.`);
        pedido.isLoading = false;
      }
    });
  }
}
