import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { Observable } from 'rxjs';

// ← ajuste o caminho se seu serviço estiver em outro lugar
import { CartService } from '../../services/cart.service';

type DishId = string | number;

interface Dish {
  id: DishId;
  name: string;
  price: number;
}

interface CartItem {
  dish: Dish;
  quantidade: number;
}

@Component({
  selector: 'app-checkout',
  templateUrl: './checkout.component.html',
})
export class CheckoutComponent implements OnInit {
  items$!: Observable<CartItem[]>;
  total$!: Observable<number>;
  customerForm!: FormGroup;

  isLoading = false;
  error = '';
  successMessage = '';

  constructor(
    private fb: FormBuilder,
    private cart: CartService // ✅ agora é uma CLASSE concreta, serve como token de DI
  ) {}

  ngOnInit(): void {
    this.items$ = this.cart.items$ as Observable<CartItem[]>;
    this.total$ = this.cart.total$ as Observable<number>;

    this.customerForm = this.fb.group({
      nomeCliente: [''],
      telefoneCliente: [''],
      enderecoCliente: [''],
    });
  }

  // Botão "−"
  dec(item: CartItem): void {
    const id = item?.dish?.id;
    if (id == null) return;

    if (item.quantidade <= 1) {
      this.cart.remove(id);
    } else {
      this.cart.decrement(id);
    }
  }

  // Botão "+"
  inc(item: CartItem): void {
    const id = item?.dish?.id;
    if (id == null) return;
    this.cart.increment(id);
  }

  // Botão "Remover"
  remove(item: CartItem): void {
    const id = item?.dish?.id;
    if (id == null) return;
    this.cart.remove(id);
  }

  onSubmit(): void {
    // sua lógica de envio
    this.successMessage = 'Pedido enviado!';
  }
}
<!-- Espaço abaixo do header fixo (no app.component.html) -->
<div class="h-2"></div>

<!-- ======= TÍTULO ======= -->
<section class="max-w-6xl mx-auto px-4 pt-6 pb-6 text-center">
  <h1 class="text-3xl font-extrabold text-gray-900">Carrinho</h1>
  <p class="text-gray-500 mt-2">Revise seus itens e finalize o pedido</p>
</section>

<!-- ======= GRID PRINCIPAL ======= -->
<div class="max-w-6xl mx-auto px-4 pb-24 grid grid-cols-1 md:grid-cols-2 gap-8">

  <!-- ======= ESQUERDA: ITENS DO CARRINHO ======= -->
  <div class="rounded-2xl border border-gray-200 bg-white shadow-sm">
    <!-- Cabeçalho laranja -->
    <div class="bg-[#ff6f5e] text-white px-4 py-3 font-semibold rounded-t-2xl">Carrinho</div>

    <!-- Cabeçalho “tabela” -->
    <div class="grid grid-cols-12 bg-[#ff6f5e] text-white text-sm font-medium border-y border-white/30">
      <div class="col-span-6 px-4 py-2">Nome</div>
      <div class="col-span-3 px-4 py-2">Preço</div>
      <div class="col-span-3 px-4 py-2">Ação</div>
    </div>

    <!-- Linhas de itens -->
    <ng-container *ngIf="(items$ | async) as items">
      <div *ngFor="let item of items"
           class="grid grid-cols-12 items-center bg-[#ffd316] border-b border-white/40 last:border-b-0">

        <!-- Nome -->
        <div class="col-span-6 px-4 py-3 text-gray-900 font-medium">
          {{ item.quantidade }}x {{ item.dish.name }}
        </div>

        <!-- Preço -->
        <div class="col-span-3 px-4 py-3 text-gray-900 font-semibold">
          {{ (item.dish.price * item.quantidade) | currency:'BRL' }}
        </div>

        <!-- Ações -->
        <div class="col-span-3 px-4 py-3">
          <div class="flex items-center gap-2 whitespace-nowrap overflow-visible">
            <button type="button"
                    (click)="dec(item)"
                    [disabled]="item.quantidade <= 1"
                    class="w-7 h-7 rounded-full border border-gray-300 bg-white text-gray-700 grid place-items-center disabled:opacity-50 disabled:cursor-not-allowed"
                    aria-label="Diminuir">
              −
            </button>

            <span class="inline-flex items-center justify-center min-w-[2.25rem] h-7 rounded-full bg-white border border-gray-300 text-gray-800 text-sm select-none">
              {{ item.quantidade }}
            </span>

            <button type="button"
                    (click)="inc(item)"
                    class="w-7 h-7 rounded-full border border-gray-300 bg-white text-gray-700 grid place-items-center"
                    aria-label="Aumentar">
              +
            </button>

            <button type="button"
                    (click)="remove(item)"
                    class="ml-2 px-3 py-1.5 rounded-md border border-gray-300 bg-white text-gray-700 text-sm">
              Remover
            </button>
          </div>
        </div>
      </div>

      <!-- Total -->
      <div class="px-4 py-4 flex justify-end text-gray-900">
        <div class="text-right">
          <span class="font-semibold">Total:</span>
          <span class="ml-2 font-bold">{{ (total$ | async) | currency:'BRL' }}</span>
        </div>
      </div>

      <!-- Vazio -->
      <div *ngIf="items.length === 0" class="py-10 text-center text-gray-500">
        Seu carrinho está vazio.
      </div>
    </ng-container>

    <!-- Erro -->
    <div *ngIf="error" class="m-4 bg-red-50 text-red-700 border border-red-200 rounded-lg px-4 py-3">
      {{ error }}
    </div>
  </div>

  <!-- ======= DIREITA: FORM ======= -->
  <div class="rounded-2xl border border-gray-200 bg-white shadow-sm">
    <!-- Cabeçalho laranja -->
    <div class="bg-[#ff6f5e] text-white px-4 py-3 font-semibold rounded-t-2xl">Fazer Pedido</div>

    <div class="p-5">
      <form [formGroup]="customerForm" (ngSubmit)="onSubmit()" class="space-y-5">
        <div>
          <label class="block text-sm font-medium text-gray-700 mb-1">Cliente</label>
          <input type="text" formControlName="nomeCliente" placeholder="Nome do cliente"
                 class="w-full px-4 py-2 rounded-lg border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-200">
        </div>

        <div>
          <label class="block text-sm font-medium text-gray-700 mb-1">Telefone</label>
          <input type="tel" formControlName="telefoneCliente" placeholder="(11) 99999-9999"
                 class="w-full px-4 py-2 rounded-lg border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-200">
        </div>

        <div>
          <label class="block text-sm font-medium text-gray-700 mb-1">Endereço</label>
          <input type="text" formControlName="enderecoCliente" placeholder="Rua, número, bairro, cidade..."
                 class="w-full px-4 py-2 rounded-lg border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-200">
        </div>

        <div class="pt-2">
          <button type="submit" [disabled]="isLoading"
                  class="px-5 w-full py-3 rounded-lg text-white font-semibold transition
                         disabled:bg-gray-400 disabled:cursor-not-allowed bg-[#ff6f5e] hover:opacity-90">
            {{ isLoading ? 'Enviando Pedido...' : 'Fazer Pedido' }}
          </button>
        </div>

        <div *ngIf="successMessage"
             class="bg-green-50 text-green-700 border border-green-200 rounded-lg px-4 py-3">
          {{ successMessage }}
        </div>
      </form>
    </div>
  </div>
</div>
