import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { PedidoService, PedidoCompleto } from '../../services/pedido.service';

@Component({
  selector: 'app-order-tracker',
  standalone: true,
  imports: [
    CommonModule,
  ],
  templateUrl: './order-tracker.component.html',
  styleUrls: ['./order-tracker.component.css']
})
export class OrderTrackerComponent {

  pedidos: PedidoCompleto[] = [];
  isLoading = false;
  error: string | null = null;

  constructor(
    private pedidoService: PedidoService
  ) {}
  // Chamado quando o cliente clica em "Buscar Pedido"
  ngOnInit(): void {
    this.carregarPedidos();
  }

  carregarPedidos(): void {
    this.isLoading = true;
    this.error = null;

    // Usando o novo método do serviço
    this.pedidoService.getAllPedidos().subscribe({
      next: (data) => {
        // Ordena os pedidos do mais novo para o mais antigo (opcional, mas bom)
        this.pedidos = data.sort((a, b) => (b.id! > a.id! ? 1 : -1));
        this.isLoading = false;
      },
      error: (err) => {
        console.error('Erro ao buscar pedidos:', err);
        this.error = 'Não foi possível carregar os pedidos. Tente novamente mais tarde.';
        this.isLoading = false;
      }
    });
  }

  // REMOVIDO: onSearch()

  // FUNÇÃO AUXILIAR (da versão anterior, ainda útil)
  formatarStatus(status: string): string {
    switch (status) {
      case 'RECEBIDO':
        return 'Recebido';
      case 'EM_PREPARO':
        return 'Em Preparo';
      case 'SAIU_PARA_ENTREGA':
        return 'A Caminho';
      case 'ENTREGUE': // Adicionei 'PRONTO' baseado na sua imagem
      case 'PRONTO':
        return 'Pronto';
      case 'CANCELADO':
        return 'Cancelado';
      default:
        return status.replace('_', ' '); // Formata qualquer outro status
    }
  }
}
